/**
 * Startup health check to ensure database connectivity
 * This prevents the common DATABASE_URL issues during remixing
 */
import { db as getDb } from "./db";
import { connectToMongoDB } from "./mongodb";
import { mongoVersionCheck } from "./health-check";

export async function performStartupCheck(): Promise<boolean> {
  try {
    console.log("ðŸ” Performing startup health check...");

    // Check if DATABASE_URL is available
    if (!process.env.DATABASE_URL) {
      console.error("âŒ DATABASE_URL environment variable is not set");
      return false;
    }

    // Test PostgreSQL connectivity with a simple connection test
    const db = getDb();
    // Use $executeRaw to avoid cached plan issues
    await db.$executeRaw`SELECT 1`;
    console.log("âœ… PostgreSQL connection successful");

    // Test MongoDB connectivity and version check
    try {
      await connectToMongoDB();
      console.log("âœ… MongoDB connection successful");

      // Perform MongoDB version check
      await mongoVersionCheck();
    } catch (mongoError) {
      console.log("âš ï¸ MongoDB connection failed (continuing with PostgreSQL only)");
      console.log("ðŸ’¡ Tip: Check your MongoDB configuration and connection string");
      console.log("ðŸ“‹ Local MongoDB: Ensure service is running on port 27017");
      console.log("ðŸŒ Atlas MongoDB: Verify IP whitelist includes 0.0.0.0/0 for development");
    }
    console.log("âœ… Startup health check passed");
    return true;

  } catch (error) {
    console.error("âŒ Startup health check failed:", error);
    console.error("ðŸ’¡ Tip: Make sure PostgreSQL database is provisioned and DATABASE_URL is set");
    return false;
  }
}

