@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

/* KIT-Canteen Design System - Zomato Inspired */

@layer base {
  :root {
    /* Core Brand Colors */
    --background: 0 0% 100%;
    --foreground: 0 0% 13%;

    /* Zomato Red Theme */
    --primary: 349 84% 57%; /* #E23744 */
    --primary-light: 349 84% 67%;
    --primary-dark: 349 84% 47%;
    --primary-foreground: 0 0% 98%;


    /* UI Components */
    --card: 0 0% 100%;
    --card-foreground: 0 0% 13%;

    --secondary: 0 0% 96%;
    --secondary-foreground: 0 0% 13%;

    --muted: 0 0% 96%;
    --muted-foreground: 0 0% 45%;

    --accent: 349 84% 95%;
    --accent-foreground: 349 84% 47%;

    /* Food App Specific */
    --success: 120 93% 34%; /* Green for veg */
    --success-foreground: 0 0% 98%;

    --warning: 38 92% 50%; /* Orange for offers */
    --warning-foreground: 0 0% 98%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 98%;

    /* Form Elements */
    --border: 0 0% 90%;
    --input: 0 0% 96%;
    --ring: 349 84% 57%;

    /* Gradients */
    --gradient-primary: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--primary-light)));
    --gradient-card: linear-gradient(145deg, hsl(0 0% 100%), hsl(0 0% 98%));

    /* Shadows */
    --shadow-card: 0 2px 8px hsl(0 0% 0% / 0.08);
    --shadow-button: 0 4px 12px hsl(var(--primary) / 0.3);

    --radius: 0.5rem;

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
}

/* Barcode Scanner Styles */
.scanner-active {
  visibility: hidden;
}

.scanner-active .cameraView {
  visibility: visible;
}

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;

    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;

    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;

    --primary: 349 84% 57%;
    --primary-light: 349 84% 67%;
    --primary-dark: 349 84% 47%;
    --primary-foreground: 210 40% 98%;

    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;

    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;

    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;

    --success: 120 93% 34%;
    --success-foreground: 210 40% 98%;

    --warning: 38 92% 50%;
    --warning-foreground: 210 40% 98%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-inter;
  }
}

@layer base {
  .font-inter {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
  }
}

/* Custom animations for mobile app feel */
@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.3s ease-out;
  }

  .animate-slide-up {
    animation: slideUp 0.3s ease-out;
  }

  .animate-bounce-in {
    animation: bounceIn 0.5s ease-out;
  }

  .animate-pulse-glow {
    animation: pulseGlow 2s ease-in-out infinite;
  }

  .gradient-primary {
    background: var(--gradient-primary);
  }

  .gradient-hero {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary-light)) 50%, hsl(var(--warning)) 100%);
  }

  .gradient-card-hover {
    background: linear-gradient(145deg, hsl(var(--card)), hsl(var(--accent)));
  }

  .shadow-card {
    box-shadow: var(--shadow-card);
  }

  .shadow-button {
    box-shadow: var(--shadow-button);
  }

  .shadow-hero {
    box-shadow: 0 20px 40px hsl(var(--primary) / 0.3);
  }

  .hover-scale {
    transition: transform 0.2s ease-out;
  }

  .hover-scale:hover {
    transform: scale(1.02);
  }

  .glass-effect {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0;
    transform: translateY(20px);
  }
  to { 
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes bounceIn {
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    opacity: 1;
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.4);
  }
  50% {
    box-shadow: 0 0 30px hsl(var(--primary) / 0.8);
  }
}

/* Toast styles removed - now using full-screen mandatory update modal */

/* Enhanced pull-to-refresh animations */
.pull-to-refresh-glow {
  animation: pullGlow 2s ease-in-out infinite;
}

.pull-to-refresh-pulse {
  animation: pullPulse 1.5s ease-in-out infinite;
}

.pull-to-refresh-bounce {
  animation: pullBounce 0.6s ease-out;
}

@keyframes pullGlow {
  0%, 100% { 
    box-shadow: 0 0 20px rgba(34, 197, 94, 0.3);
  }
  50% { 
    box-shadow: 0 0 30px rgba(34, 197, 94, 0.5);
  }
}

@keyframes pullPulse {
  0%, 100% { 
    transform: scale(1);
    opacity: 0.7;
  }
  50% { 
    transform: scale(1.05);
    opacity: 1;
  }
}

@keyframes pullBounce {
  0% { transform: scale(1); }
  25% { transform: scale(1.1); }
  50% { transform: scale(0.95); }
  75% { transform: scale(1.05); }
  100% { transform: scale(1); }
}

/* Shimmer effect for refresh indicator */
.pull-shimmer {
  background: linear-gradient(90deg, 
    transparent, 
    rgba(255, 255, 255, 0.4), 
    transparent
  );
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
}

@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

