                    <SelectItem value="Basic">Basic (6+ characters)</SelectItem>
                    <SelectItem value="Standard">Standard (8+ chars, mixed case)</SelectItem>
                    <SelectItem value="Strong">Strong (12+ chars, symbols)</SelectItem>
