      toast({
        title: "User Deleted",
        description: `${userName} has been deleted successfully`,
      });
      setDeleteDialog({open: false, user: null});

      // Force cache invalidation and refetch
      await queryClient.invalidateQueries({ queryKey: ['/api/users'] });
