import { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, CheckCircle, Plus, Trash2, ShoppingCart, AlertTriangle, Clock, User, CreditCard, Receipt, Package } from "lucide-react";
import { toast } from "sonner";
import { formatOrderIdDisplay } from "../../../shared/utils";
import { useWebPushNotifications } from "@/hooks/useWebPushNotifications";
import NotificationPermissionDialog from "@/components/NotificationPermissionDialog";

// Types for our components
interface MenuItem {
  id: number;
  name: string;
  price: number;
  category: string;
  available: boolean;
  stock: number;
}

interface SelectedItem extends MenuItem {
  quantity: number;
}

interface OrderDetails {
  id: string;
  status: string;
  placedAt: string;
  customerName: string;
  items: SelectedItem[];
  total: number;
  estimatedTime?: number;
}

export default function BarcodeScannerPage() {
  const [, setLocation] = useLocation();
  const [orderId, setOrderId] = useState("");

  // Manual order creation state
  const [isMenuModalOpen, setIsMenuModalOpen] = useState(false);
  const [selectedItems, setSelectedItems] = useState<SelectedItem[]>([]);
  const [showNotificationDialog, setShowNotificationDialog] = useState(false);

  // Order details popup state
  const [isOrderDetailsModalOpen, setIsOrderDetailsModalOpen] = useState(false);
  const [orderDetails, setOrderDetails] = useState<OrderDetails | null>(null);
  const [isLoadingOrder, setIsLoadingOrder] = useState(false);

  // Get user data and notification hook
  const userData = JSON.parse(localStorage.getItem('user') || '{}');
  const { permission, supportsNotifications } = useWebPushNotifications(userData?.id, userData?.role);

  // Sample menu items - in real app, this would come from API
  const menuItems: MenuItem[] = [
    { id: 1, name: "Veg Thali", price: 60, category: "Veg", available: true, stock: 20 },
    { id: 2, name: "Paneer Curry", price: 70, category: "Veg", available: true, stock: 15 },
    { id: 3, name: "Rice", price: 25, category: "Staples", available: true, stock: 100 },
    { id: 4, name: "Coffee", price: 15, category: "Beverages", available: true, stock: 50 },
    { id: 5, name: "Chicken Curry", price: 80, category: "Non-Veg", available: true, stock: 10 },
  ];

  // Function to fetch order details from API
  const fetchOrderDetails = async (orderIdToFetch: string): Promise<OrderDetails | null> => {
    try {
      const response = await fetch(`/api/orders/${orderIdToFetch}`);
      if (response.ok) {
        const order = await response.json();
        // Transform the API response to match our OrderDetails interface
        return {
          id: order.orderNumber || orderIdToFetch,
          status: order.status || "completed",
          placedAt: order.createdAt || "Just now",
          customerName: order.customerName || "Unknown Customer",
          items: JSON.parse(order.items || "[]"),
          total: order.amount || 0,
          estimatedTime: order.estimatedTime
        };
      } else {
        // Return mock data if API fails (for demo purposes)
        return {
          id: orderIdToFetch,
          status: "completed",
          placedAt: "Today, 2:30 PM",
          customerName: "John Doe",
          items: [
            { id: 1, name: "Veg Thali", price: 60, category: "Veg", available: true, stock: 20, quantity: 1 },
            { id: 4, name: "Coffee", price: 15, category: "Beverages", available: true, stock: 50, quantity: 2 }
          ],
          total: 90,
          estimatedTime: 15
        };
      }
    } catch (error) {
      console.error("Error fetching order details:", error);
      return null;
    }
  };

  const handleSubmit = async () => {
    if (!orderId.trim()) {
      toast.error("Please enter an Order ID");
      return;
    }

    // Validate 12-digit numeric format
    const orderIdPattern = /^[0-9]{12}$/;
    if (!orderIdPattern.test(orderId)) {
      toast.error("Order ID must be exactly 12 digits (numbers only)");
      return;
    }

    setIsLoadingOrder(true);
    toast.success(`Processing Order ID: ${orderId}`);

    // Fetch order details and show in popup instead of navigating
    const details = await fetchOrderDetails(orderId);
    if (details) {
      setOrderDetails(details);
      setIsOrderDetailsModalOpen(true);
      setOrderId(""); // Clear the input
    } else {
      toast.error("Order not found or failed to load details");
    }
    setIsLoadingOrder(false);
  };

  // Manual order creation handlers
  const handleAddItemToOrder = (menuItem: MenuItem, quantity = 1) => {
    const existingItem = selectedItems.find(item => item.id === menuItem.id);
    if (existingItem) {
      setSelectedItems(selectedItems.map(item => 
        item.id === menuItem.id 
          ? { ...item, quantity: item.quantity + quantity }
          : item
      ));
    } else {
      setSelectedItems([...selectedItems, { ...menuItem, quantity }]);
    }
  };

  const handleRemoveItemFromOrder = (itemId: number) => {
    setSelectedItems(selectedItems.filter(item => item.id !== itemId));
  };

  const handleUpdateItemQuantity = (itemId: number, quantity: number) => {
    if (quantity <= 0) {
      handleRemoveItemFromOrder(itemId);
      return;
    }
    setSelectedItems(selectedItems.map(item => 
      item.id === itemId ? { ...item, quantity } : item
    ));
  };

  const calculateOrderTotal = () => {
    return selectedItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handleCreateManualOrder = async () => {
    if (selectedItems.length === 0) {
      toast.error("Please add at least one item");
      return;
    }

    // Check notification permissions before placing order
    if (supportsNotifications && permission !== 'granted') {
      setShowNotificationDialog(true);
      return;
    }

    // Proceed with order creation
    proceedWithManualOrder();
  };

  const proceedWithManualOrder = async () => {
    try {
      const orderData = {
        customerId: userData?.id || 6, // Use current user ID or fallback
        customerName: userData?.name || "Manual Order",
        items: JSON.stringify(selectedItems),
        amount: calculateOrderTotal(),
        status: "preparing",
        estimatedTime: 15
      };

      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(orderData)
      });

      if (response.ok) {
        const order = await response.json();
        toast.success(`Order created: ${order.orderNumber} - Total: â‚¹${order.amount}`);

        // Reset form and close modal
        setSelectedItems([]);
        setIsMenuModalOpen(false);
      } else {
        toast.error("Failed to create order");
      }
    } catch (error) {
      toast.error("Error creating order");
    }
  };

  const handleNotificationDialogClose = (granted: boolean) => {
    setShowNotificationDialog(false);
    if (granted) {
      // User granted permission, proceed with order
      proceedWithManualOrder();
    }
    // If denied, do nothing - let user try again if they want
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b border-border p-4">
        <div className="flex items-center justify-between">
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => setLocation("/canteen-owner")}
            className="flex items-center"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back
          </Button>
          <h1 className="text-lg font-semibold">Order Entry</h1>
          <Button 
            onClick={() => setIsMenuModalOpen(true)}
            variant="outline"
            size="sm"
          >
            <ShoppingCart className="w-4 h-4 mr-2" />
            Quick Order
          </Button>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Order ID Entry Section */}
        <Card className="shadow-card">
          <CardHeader>
            <CardTitle>Enter Order ID</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="orderId">Order ID</Label>
              <Input
                id="orderId"
                placeholder="e.g., 123456789012"
                value={orderId}
                onChange={(e) => setOrderId(e.target.value.replace(/[^0-9]/g, ''))}
                maxLength={12}
              />
            </div>

            {orderId && (
              <Alert className={
                /^[0-9]{12}$/.test(orderId) 
                  ? "border-green-500/50 text-green-600 dark:border-green-500 [&>svg]:text-green-600"
                  : "border-amber-500/50 text-amber-600 dark:border-amber-500 [&>svg]:text-amber-600"
              }>
                {/^[0-9]{12}$/.test(orderId) ? (
                  <CheckCircle className="h-4 w-4" />
                ) : (
                  <AlertTriangle className="h-4 w-4" />
                )}
                <AlertDescription>
                  {/^[0-9]{12}$/.test(orderId) 
                    ? (
                        <div className="flex items-center">
                          <span>Valid Order ID: </span>
                          <span>{(() => {
                            const formatted = formatOrderIdDisplay(orderId);
                            return formatted.prefix;
                          })()}</span>
                          <span className="bg-green-500/20 text-green-700 font-bold px-1 rounded ml-0">
                            {(() => {
                              const formatted = formatOrderIdDisplay(orderId);
                              return formatted.highlighted;
                            })()}
                          </span>
                        </div>
                      )
                    : `Invalid format. Expected 12 digits (0-9): ${orderId.length}/12`
                  }
                </AlertDescription>
              </Alert>
            )}

            <Button 
              onClick={handleSubmit}
              className="w-full"
              size="lg"
              disabled={!orderId.trim() || isLoadingOrder}
            >
              {isLoadingOrder ? "Loading..." : "Submit Order ID"}
            </Button>
          </CardContent>
        </Card>

        {/* Quick Order Menu Modal */}
        <Dialog open={isMenuModalOpen} onOpenChange={setIsMenuModalOpen}>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Quick Order Menu</DialogTitle>
            </DialogHeader>
            <div className="space-y-6">
              {/* Menu Items Selection */}
              <div className="space-y-4">
                <h3 className="font-medium">Available Items</h3>
                <div className="grid grid-cols-1 gap-2 max-h-64 overflow-y-auto border rounded p-3">
                  {menuItems.filter(item => item.available).map((item) => (
                    <div key={item.id} className="flex items-center justify-between p-3 border rounded hover:bg-accent/50">
                      <div className="flex-1">
                        <div className="font-medium">{item.name}</div>
                        <div className="text-sm text-muted-foreground">{item.category}</div>
                        <div className="text-lg font-semibold text-primary">â‚¹{item.price}</div>
                      </div>
                      <Button
                        size="sm"
                        onClick={() => handleAddItemToOrder(item)}
                      >
                        <Plus className="w-3 h-3 mr-1" />
                        Add
                      </Button>
                    </div>
                  ))}
                </div>
              </div>

              {/* Selected Items */}
              {selectedItems.length > 0 && (
                <div className="space-y-4">
                  <h3 className="font-medium">Order Summary</h3>
                  <div className="space-y-2 max-h-32 overflow-y-auto">
                    {selectedItems.map((item) => (
                      <div key={item.id} className="flex items-center justify-between p-3 bg-accent/50 rounded">
                        <div>
                          <span className="font-medium">{item.name}</span>
                          <span className="text-sm text-muted-foreground ml-2">â‚¹{item.price} each</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleUpdateItemQuantity(item.id, item.quantity - 1)}
                          >
                            -
                          </Button>
                          <span className="w-8 text-center font-medium">{item.quantity}</span>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleUpdateItemQuantity(item.id, item.quantity + 1)}
                          >
                            +
                          </Button>
                          <Button
                            size="sm"
                            variant="destructive"
                            onClick={() => handleRemoveItemFromOrder(item.id)}
                          >
                            <Trash2 className="w-3 h-3" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Order Total */}
                  <div className="border-t pt-4">
                    <div className="flex justify-between items-center mb-4">
                      <span className="text-lg font-semibold">Total:</span>
                      <span className="text-2xl font-bold text-primary">â‚¹{calculateOrderTotal()}</span>
                    </div>

                    {/* Action Buttons */}
                    <div className="flex space-x-2">
                      <Button
                        variant="outline"
                        onClick={() => {
                          setSelectedItems([]);
                          setIsMenuModalOpen(false);
                        }}
                        className="flex-1"
                      >
                        Cancel
                      </Button>
                      <Button
                        onClick={handleCreateManualOrder}
                        disabled={selectedItems.length === 0}
                        className="flex-1"
                        size="lg"
                      >
                        Create Order
                      </Button>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </DialogContent>
        </Dialog>

        {/* Order Details Popup Modal */}
        <Dialog open={isOrderDetailsModalOpen} onOpenChange={setIsOrderDetailsModalOpen}>
          <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center space-x-2">
                <Package className="w-5 h-5" />
                <span>Order Details</span>
              </DialogTitle>
            </DialogHeader>

            {orderDetails && (
              <div className="space-y-6">
                {/* Order Status */}
                <div className="text-center space-y-2">
                  <div className="flex justify-center">
                    <Badge className={
                      orderDetails.status === "completed" ? "bg-green-500 text-white" :
                      orderDetails.status === "ready" ? "bg-blue-500 text-white" :
                      orderDetails.status === "preparing" ? "bg-orange-500 text-white" :
                      "bg-gray-500 text-white"
                    }>
                      {orderDetails.status.charAt(0).toUpperCase() + orderDetails.status.slice(1)}
                    </Badge>
                  </div>
                  <h3 className="text-lg font-semibold">Order #{orderDetails.id}</h3>
                  <p className="text-sm text-muted-foreground flex items-center justify-center">
                    <Clock className="w-4 h-4 mr-1" />
                    {orderDetails.placedAt}
                  </p>
                </div>

                {/* Customer Info */}
                <div className="space-y-2">
                  <h4 className="font-medium flex items-center">
                    <User className="w-4 h-4 mr-2" />
                    Customer
                  </h4>
                  <p className="text-sm text-muted-foreground pl-6">{orderDetails.customerName}</p>
                </div>

                {/* Order Items */}
                <div className="space-y-3">
                  <h4 className="font-medium flex items-center">
                    <Receipt className="w-4 h-4 mr-2" />
                    Items ({orderDetails.items.length})
                  </h4>
                  <div className="space-y-2">
                    {orderDetails.items.map((item) => (
                      <div key={item.id} className="flex items-center justify-between p-2 bg-accent/50 rounded">
                        <div className="flex-1">
                          <p className="font-medium text-sm">{item.name}</p>
                          <p className="text-xs text-muted-foreground">â‚¹{item.price} Ã— {item.quantity}</p>
                        </div>
                        <div className="text-sm font-semibold">â‚¹{item.price * item.quantity}</div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Total */}
                <div className="border-t pt-4">
                  <div className="flex items-center justify-between">
                    <span className="font-medium flex items-center">
                      <CreditCard className="w-4 h-4 mr-2" />
                      Total Amount
                    </span>
                    <span className="text-lg font-bold text-primary">â‚¹{orderDetails.total}</span>
                  </div>
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-2 pt-4">
                  <Button
                    variant="outline"
                    onClick={() => setIsOrderDetailsModalOpen(false)}
                    className="flex-1"
                  >
                    Close
                  </Button>
                  {orderDetails.status === "ready" && (
                    <Button
                      onClick={() => {
                        toast.success("Order marked as completed!");
                        setIsOrderDetailsModalOpen(false);
                      }}
                      className="flex-1"
                    >
                      Mark Complete
                    </Button>
                  )}
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>

        {/* Notification Permission Dialog */}
        <NotificationPermissionDialog
          isOpen={showNotificationDialog}
          onClose={handleNotificationDialogClose}
          userId={userData?.id}
          userRole={userData?.role}
        />
      </div>
    </div>
  );
}

client/src/components/BottomNavigation.t.content:
import { useLocation } from "wouter";
import { Home, ShoppingCart, Clock, User, UtensilsCrossed } from "lucide-react";

interface BottomNavigationProps {
  currentPage: "home" | "menu" | "cart" | "orders" | "profile";
}

export default function BottomNavigation({ currentPage }: BottomNavigationProps) {
  const [, setLocation] = useLocation();

  const navigationItems = [
    { id: "home", label: "Home", icon: Home, route: "/home" },
    { id: "menu", label: "Menu", icon: UtensilsCrossed, route: "/menu/all" },
    { id: "cart", label: "Cart", icon: ShoppingCart, route: "/cart" },
    { id: "orders", label: "Orders", icon: Clock, route: "/orders" },
    { id: "profile", label: "Profile", icon: User, route: "/profile" }
  ];

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-border z-[100]">
      <div className="flex">
        {navigationItems.map((item) => {
          const isActive = currentPage === item.id;
          return (
            <button
              key={item.id}
              onClick={() => setLocation(item.route)}
              className={`flex-1 flex flex-col items-center py-3 px-2 transition-colors ${
                isActive 
                  ? "text-primary" 
                  : "text-muted-foreground hover:text-foreground"
              }`}
            >
              <item.icon className={`w-5 h-5 ${isActive ? "fill-current" : ""}`} />
              <span className="text-xs mt-1 font-medium">{item.label}</span>
              {isActive && (
                <div className="w-1 h-1 bg-primary rounded-full mt-1"></div>
              )}
            </button>
          );
        })}
      </div>
    </div>
  );
}

client/src/components/CanteenOrderDetail.content:
import { useLocation, useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { toast } from "sonner";
import { 
  ArrowLeft, 
  Clock, 
  Receipt, 
  User,
  Phone,
  CreditCard,
  CheckCircle,
  XCircle,
  ChefHat,
  Loader2,
  Mail,
  School,
  BadgeCheck,
  GraduationCap,
  Building,
  Calendar
} from "lucide-react";
import type { Order, User as UserType } from "@shared/schema";
import { formatOrderIdDisplay } from "@shared/utils";

export default function CanteenOrderDetailPage() {
  const [, setLocation] = useLocation();
  const { orderId } = useParams();
  const queryClient = useQueryClient();

  // Fetch real order data from database, supporting both ID and barcode lookup
  const { data: orderDetails, isLoading, error } = useQuery<Order>({
    queryKey: ['/api/orders', orderId],
    queryFn: async () => {
      // Try to fetch by database ID first
      try {
        const response = await fetch(`/api/orders/${orderId}`);
        if (response.ok) {
          return response.json();
        }
      } catch (error) {
        // If ID lookup fails, continue to barcode lookup
      }

      // Fallback: search all orders for matching barcode or order number
      const ordersResponse = await fetch('/api/orders');
      if (!ordersResponse.ok) {
        throw new Error('Failed to fetch orders');
      }
      const orders = await ordersResponse.json();
      const order = orders.find((o: Order) => 
        o.barcode === orderId || o.orderNumber === orderId
      );

      if (!order) {
        throw new Error('Order not found');
      }

      return order;
    },
    enabled: !!orderId,
  });

  // Fetch complete user details based on customerId from order
  const { data: customerDetails, isLoading: isLoadingCustomer } = useQuery<UserType>({
    queryKey: ['/api/users', orderDetails?.customerId],
    queryFn: async () => {
      if (!orderDetails?.customerId) return null;
      const response = await fetch(`/api/users/${orderDetails.customerId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch customer details');
      }
      return response.json();
    },
    enabled: !!orderDetails?.customerId,
  });

  // Parse order items from JSON string
  const parsedItems = orderDetails?.items ? (() => {
    try {
      const itemsData = JSON.parse(orderDetails.items);
      return Array.isArray(itemsData) ? itemsData : [];
    } catch (error) {
      console.error('Error parsing order items:', error);
      return [];
    }
  })() : [];

  // Update order status mutation
  const updateOrderStatusMutation = useMutation({
    mutationFn: async (newStatus: string) => {
      const response = await fetch(`/api/orders/${orderId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus })
      });
      if (!response.ok) {
        throw new Error('Failed to update order status');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/orders'] });
      queryClient.invalidateQueries({ queryKey: ['/api/orders', orderId] });
      toast.success('Order status updated successfully');
    },
    onError: () => {
      toast.error('Failed to update order status');
    }
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case "pending": return "bg-warning text-warning-foreground";
      case "preparing": return "bg-blue-500 text-white";
      case "ready": return "bg-success text-success-foreground";
      case "completed": return "bg-muted text-muted-foreground";
      default: return "bg-muted text-muted-foreground";
    }
  };

  const handleStatusUpdate = (newStatus: string) => {
    updateOrderStatusMutation.mutate(newStatus);
  };

  const handleMarkReady = () => {
    handleStatusUpdate("ready");
  };

  const handleCompleteOrder = () => {
    handleStatusUpdate("completed");
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="flex items-center space-x-2">
          <Loader2 className="w-6 h-6 animate-spin" />
          <span>Loading order details...</span>
        </div>
      </div>
    );
  }

  if (error || !orderDetails) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-2">Order Not Found</h2>
          <p className="text-muted-foreground mb-4">The order you're looking for doesn't exist.</p>
          <Button onClick={() => setLocation('/canteen-owner')}>Back to Dashboard</Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-card border-b px-4 py-4 sticky top-0 z-10">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button variant="ghost" size="icon" onClick={() => {
              if (window.history.length > 1) {
                window.history.back();
              } else {
                setLocation('/canteen-owner');
              }
            }}>
              <ArrowLeft className="w-5 h-5" />
            </Button>
            <div>
              <h1 className="text-xl font-bold">Order Details</h1>
              <p className="text-sm text-muted-foreground">
                Order #{(() => {
                  const formatted = formatOrderIdDisplay(orderDetails.orderNumber || orderDetails.id.toString());
                  return (
                    <>
                      {formatted.prefix}
                      <span className="bg-primary/20 text-primary font-bold px-1 rounded ml-1">
                        {formatted.highlighted}
                      </span>
                    </>
                  );
                })()}
              </p>
            </div>
          </div>
          <Badge className={getStatusColor(orderDetails.status)}>
            {orderDetails.status.charAt(0).toUpperCase() + orderDetails.status.slice(1)}
          </Badge>
        </div>
      </div>

      <div className="p-4 space-y-6">
        {/* Order Status & Actions */}
        <Card className="shadow-card">
          <CardContent className="p-4">
            <div className="flex items-center justify-between mb-4">
              <div className="space-y-1">
                <p className="text-sm text-muted-foreground">Order placed at</p>
                <p className="font-semibold">{new Date(orderDetails.createdAt).toLocaleString()}</p>
              </div>
              <div className="text-right">
                <p className="text-sm text-muted-foreground">Estimated time</p>
                <p className="font-semibold">{orderDetails.estimatedTime || 15} mins</p>
              </div>
            </div>

            {/* Action Buttons based on status */}
            <div className="flex gap-2">
              {orderDetails.status === "preparing" && (
                <Button 
                  onClick={handleMarkReady}
                  className="w-full bg-success text-success-foreground hover:bg-success/90"
                  disabled={updateOrderStatusMutation.isPending}
                >
                  <ChefHat className="w-4 h-4 mr-2" />
                  Mark as Ready
                </Button>
              )}

              {orderDetails.status === "ready" && (
                <Button 
                  onClick={handleCompleteOrder}
                  className="w-full bg-success text-success-foreground hover:bg-success/90"
                  disabled={updateOrderStatusMutation.isPending}
                >
                  <CheckCircle className="w-4 h-4 mr-2" />
                  Complete Order
                </Button>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Order Items */}
        <Card className="shadow-card">
          <CardContent className="p-4">
            <div className="flex items-center justify-between p-4 bg-muted/20 rounded-lg mb-4">
              <span className="font-medium">Order Items ({parsedItems.length})</span>
              <Receipt className="w-5 h-5 text-muted-foreground" />
            </div>

            <div className="space-y-3">
              {parsedItems.map((item: any, index: number) => (
                <div key={index} className="flex items-center space-x-4 p-4 border rounded-lg">
                  <div className="w-12 h-12 rounded-lg bg-accent/50 flex items-center justify-center">
                    <span className="text-2xl">ðŸ½ï¸</span>
                  </div>

                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-1">
                      <h4 className="font-medium">{item.name || 'Unknown Item'}</h4>
                      <Badge variant="secondary" className="bg-green-100 text-green-700 text-xs">VEG</Badge>
                    </div>
                    <p className="text-sm text-muted-foreground">â‚¹{item.price || 0} each</p>
                    {item.specialInstructions && (
                      <p className="text-sm text-primary mt-1">Special Instructions: {item.specialInstructions}</p>
                    )}
                  </div>

                  <div className="text-right">
                    <p className="font-semibold">x{item.quantity || 1}</p>
                    <p className="text-sm font-bold">â‚¹{(item.price || 0) * (item.quantity || 1)}</p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Customer Details */}
        <Card className="shadow-card">
          <CardContent className="p-4">
            <h2 className="font-semibold mb-4 flex items-center">
              <User className="w-5 h-5 mr-2" />
              Customer Details
            </h2>

            {isLoadingCustomer ? (
              <div className="flex items-center space-x-2 py-4">
                <Loader2 className="w-4 h-4 animate-spin text-muted-foreground" />
                <span className="text-sm text-muted-foreground">Loading customer details...</span>
              </div>
            ) : customerDetails ? (
              <div className="space-y-4">
                {/* Basic Info */}
                <div className="space-y-3">
                  <div className="flex items-center space-x-3">
                    <User className="w-4 h-4 text-muted-foreground" />
                    <div>
                      <span className="font-medium">{customerDetails.name}</span>
                      <Badge variant="outline" className="ml-2 text-xs">
                        {customerDetails.role.charAt(0).toUpperCase() + customerDetails.role.slice(1)}
                      </Badge>
                    </div>
                  </div>

                  <div className="flex items-center space-x-3">
                    <Mail className="w-4 h-4 text-muted-foreground" />
                    <span className="text-sm">{customerDetails.email}</span>
                  </div>

                  {customerDetails.phoneNumber && (
                    <div className="flex items-center space-x-3">
                      <Phone className="w-4 h-4 text-muted-foreground" />
                      <span className="text-sm">{customerDetails.phoneNumber}</span>
                    </div>
                  )}
                </div>

                <Separator />

                {/* Role-specific details */}
                {customerDetails.role === 'student' && (
                  <div className="space-y-3">
                    <h3 className="text-sm font-medium text-muted-foreground flex items-center">
                      <GraduationCap className="w-4 h-4 mr-2" />
                      Student Information
                    </h3>

                    {customerDetails.registerNumber && (
                      <div className="flex items-center space-x-3">
                        <BadgeCheck className="w-4 h-4 text-muted-foreground" />
                        <div>
                          <span className="text-sm font-medium">Register Number:</span>
                          <span className="text-sm ml-2">{customerDetails.registerNumber}</span>
                        </div>
                      </div>
                    )}

                    {customerDetails.department && (
                      <div className="flex items-center space-x-3">
                        <School className="w-4 h-4 text-muted-foreground" />
                        <div>
                          <span className="text-sm font-medium">Department:</span>
                          <span className="text-sm ml-2">{customerDetails.department}</span>
                        </div>
                      </div>
                    )}

                    {customerDetails.currentStudyYear && (
                      <div className="flex items-center space-x-3">
                        <Calendar className="w-4 h-4 text-muted-foreground" />
                        <div>
                          <span className="text-sm font-medium">Current Year:</span>
                          <span className="text-sm ml-2">{customerDetails.currentStudyYear}{customerDetails.currentStudyYear === 1 ? 'st' : customerDetails.currentStudyYear === 2 ? 'nd' : customerDetails.currentStudyYear === 3 ? 'rd' : 'th'} Year</span>
                        </div>
                      </div>
                    )}

                    {customerDetails.joiningYear && (
                      <div className="flex items-center space-x-3">
                        <Calendar className="w-4 h-4 text-muted-foreground" />
                        <div>
                          <span className="text-sm font-medium">Joined:</span>
                          <span className="text-sm ml-2">{customerDetails.joiningYear}</span>
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {customerDetails.role === 'staff' && customerDetails.staffId && (
                  <div className="space-y-3">
                    <h3 className="text-sm font-medium text-muted-foreground flex items-center">
                      <Building className="w-4 h-4 mr-2" />
                      Staff Information
                    </h3>

                    <div className="flex items-center space-x-3">
                      <BadgeCheck className="w-4 h-4 text-muted-foreground" />
                      <div>
                        <span className="text-sm font-medium">Staff ID:</span>
                        <span className="text-sm ml-2">{customerDetails.staffId}</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ) : (
              <div className="space-y-3">
                <div className="flex items-center space-x-3">
                  <User className="w-4 h-4 text-muted-foreground" />
                  <span className="font-medium">{orderDetails.customerName || 'N/A'}</span>
                </div>
                <div className="flex items-center space-x-3">
