        {/* Orders List */}
        {filteredOrders.length === 0 ? (
          <Card>
            <CardContent className="p-12 text-center">
              <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
                <Receipt className="w-8 h-8 text-muted-foreground" />
              </div>
              <h3 className="text-lg font-semibold mb-2">
                {userOrders.length === 0 ? "No orders yet" : "No orders found"}
              </h3>
              <p className="text-muted-foreground mb-4">
                {userOrders.length === 0 
                  ? "Start ordering delicious food from our menu!"
                  : "Try adjusting your search or filter criteria"
                }
              </p>
              <Button onClick={() => setLocation("/home")}>
                Browse Menu
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-3">
            {filteredOrders.map((order) => (
              <Card key={order.id} className="hover:shadow-md transition-shadow">
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-3">
                    <div>
                      <h3 className="font-semibold">Order #{order.orderNumber || order.id}</h3>
                      <p className="text-sm text-muted-foreground">
                        {new Date(order.createdAt).toLocaleDateString()} at{' '}
                        {new Date(order.createdAt).toLocaleTimeString()}
                      </p>
                    </div>
                    <Badge className={getStatusColor(order.status || 'pending')}>
                      <div className="flex items-center space-x-1">
                        {getStatusIcon(order.status || 'pending')}
                        <span className="capitalize">{order.status || 'Pending'}</span>
                      </div>
                    </Badge>
                  </div>

                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-lg font-bold">â‚¹{order.amount}</p>
                      <p className="text-sm text-muted-foreground">
                        Payment: Pending
                      </p>
                    </div>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setLocation(`/order-status/${order.id}?from=orders`)}
                    >
                      View Details
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>

      </PullToRefresh>
      <BottomNavigation currentPage="orders" />
    </>
  );
}

