import React, { useState } from 'react';
import { useLocation } from 'wouter';
import { ArrowLeft, Star, Camera, Send } from 'lucide-react';
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";

const RateReviewPage = () => {
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  // Get orderId from URL search params
  const urlParams = new URLSearchParams(window.location.search);
  const orderId = urlParams.get('orderId');

  // Mock order data
  const orderData = {
    id: orderId || 'ORD001',
    items: [
      {
        id: 1,
        name: 'Grilled Chicken Burger',
        image: '/placeholder.png'
      },
      {
        id: 2,
        name: 'Crispy French Fries',
        image: '/placeholder.png'
      },
      {
        id: 3,
        name: 'Chocolate Milkshake',
        image: '/placeholder.png'
      }
    ]
  };

  const [reviews, setReviews] = useState(
    orderData.items.map(item => ({
      itemId: item.id,
      rating: 0,
      comment: '',
      photos: []
    }))
  );

  const [overallRating, setOverallRating] = useState(0);
  const [overallComment, setOverallComment] = useState('');

  const updateItemRating = (itemId: number, rating: number) => {
    setReviews(reviews.map(review =>
      review.itemId === itemId ? { ...review, rating } : review
    ));
  };

  const updateItemComment = (itemId: number, comment: string) => {
    setReviews(reviews.map(review =>
      review.itemId === itemId ? { ...review, comment } : review
    ));
  };

  const renderStars = (rating: number, onStarClick: (star: number) => void, size = "h-6 w-6") => {
    return (
      <div className="flex gap-1">
        {[1, 2, 3, 4, 5].map((star) => (
          <button
            key={star}
            onClick={() => onStarClick(star)}
            className="transition-colors"
          >
            <Star
              className={`${size} ${
                star <= rating
                  ? 'fill-yellow-400 text-yellow-400'
                  : 'text-gray-300'
              }`}
            />
          </button>
        ))}
      </div>
    );
  };

  const handleSubmit = () => {
    const hasAllRatings = reviews.every(review => review.rating > 0) && overallRating > 0;

    if (!hasAllRatings) {
      toast({
        title: "Please rate all items",
        description: "Please provide ratings for all items and overall experience.",
        variant: "destructive"
      });
      return;
    }

    toast({
      title: "Thank you for your review!",
      description: "Your feedback helps us improve our service.",
    });

    setLocation('/orders');
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="sticky top-0 z-50 bg-background border-b px-4 py-3">
        <div className="flex items-center gap-3">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setLocation('/orders')}
            className="shrink-0"
          >
            <ArrowLeft className="h-5 w-5" />
          </Button>
          <div>
            <h1 className="text-lg font-semibold">Rate & Review</h1>
            <p className="text-sm text-muted-foreground">Order #{orderData.id}</p>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="p-4 space-y-6">
        {/* Overall Rating */}
        <Card>
          <CardContent className="p-4">
            <h2 className="font-semibold mb-3">Overall Experience</h2>
            <div className="text-center space-y-3">
              {renderStars(overallRating, setOverallRating, "h-8 w-8")}
              <p className="text-sm text-muted-foreground">
                {overallRating === 0 && "Tap to rate"}
                {overallRating === 1 && "Poor"}
                {overallRating === 2 && "Fair"}
                {overallRating === 3 && "Good"}
                {overallRating === 4 && "Very Good"}
                {overallRating === 5 && "Excellent"}
              </p>
            </div>
            <Textarea
              placeholder="Share your overall experience..."
              value={overallComment}
              onChange={(e) => setOverallComment(e.target.value)}
              className="mt-3"
              rows={3}
            />
          </CardContent>
        </Card>

        {/* Individual Items */}
        <div className="space-y-4">
          <h2 className="font-semibold">Rate Individual Items</h2>

          {orderData.items.map((item) => {
            const review = reviews.find(r => r.itemId === item.id);

            return (
              <Card key={item.id}>
                <CardContent className="p-4">
                  <div className="flex gap-3 mb-3">
                    <img
                      src={item.image}
                      alt={item.name}
                      className="w-12 h-12 rounded-lg object-cover"
                    />
                    <div className="flex-1">
                      <h3 className="font-medium">{item.name}</h3>
                      <div className="mt-1">
                        {renderStars(
                          review?.rating || 0,
                          (rating) => updateItemRating(item.id, rating)
                        )}
                      </div>
                    </div>
                  </div>

                  <Textarea
                    placeholder={`What did you think of the ${item.name}?`}
                    value={review?.comment || ''}
                    onChange={(e) => updateItemComment(item.id, e.target.value)}
                    rows={2}
                    className="mb-3"
                  />

                  <Button
                    variant="outline"
                    size="sm"
                    className="w-full"
                    onClick={() => {
                      toast({
                        title: "Photo upload",
                        description: "Photo upload feature coming soon!",
                      });
                    }}
                  >
                    <Camera className="h-4 w-4 mr-2" />
                    Add Photos (Optional)
                  </Button>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Tips Section */}
        <Card className="bg-accent/50">
          <CardContent className="p-4">
            <h3 className="font-medium mb-2">Help others decide</h3>
            <ul className="text-sm text-muted-foreground space-y-1">
              <li>â€¢ Share what you liked or didn't like</li>
              <li>â€¢ Mention food quality, taste, and portion size</li>
              <li>â€¢ Add photos to show how your food looked</li>
            </ul>
          </CardContent>
        </Card>
      </div>

      {/* Bottom Action */}
      <div className="sticky bottom-0 bg-background border-t p-4">
        <Button
          onClick={handleSubmit}
          className="w-full"
          variant="food"
          size="mobile"
        >
          <Send className="h-4 w-4 mr-2" />
          Submit Review
        </Button>
      </div>
    </div>
  );
};

export default RateReviewPage;

client/src/components/ReorderPage.tsx.content:
import React, { useState } from 'react';
import { useLocation, useSearch } from 'wouter';
import { ArrowLeft, Plus, Minus, ShoppingCart } from 'lucide-react';
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";

const ReorderPage = () => {
  const [, setLocation] = useLocation();
  const searchParams = new URLSearchParams(useSearch());
  const orderId = searchParams.get('orderId');
  const { toast } = useToast();

  // Mock order data
  const orderData = {
    id: orderId || 'ORD001',
    items: [
      {
        id: 1,
        name: 'Grilled Chicken Burger',
        price: 12.99,
        image: '/placeholder.png',
        quantity: 1,
        customizations: ['No onions', 'Extra cheese']
      },
      {
        id: 2,
        name: 'Crispy French Fries',
        price: 4.99,
        image: '/placeholder.png',
        quantity: 2,
        customizations: []
      },
      {
        id: 3,
        name: 'Chocolate Milkshake',
        price: 5.99,
        image: '/placeholder.png',
        quantity: 1,
        customizations: ['Extra chocolate']
      }
    ]
  };

  const [items, setItems] = useState(orderData.items.map(item => ({
    ...item,
    reorderQuantity: item.quantity
  })));

  const updateQuantity = (itemId: number, change: number) => {
    setItems(items.map(item => 
      item.id === itemId 
        ? { ...item, reorderQuantity: Math.max(0, item.reorderQuantity + change) }
        : item
    ));
  };

  const getTotalPrice = () => {
    return items.reduce((total, item) => total + (item.price * item.reorderQuantity), 0);
  };

  const getSelectedItemsCount = () => {
    return items.reduce((count, item) => count + item.reorderQuantity, 0);
  };

  const handleReorder = () => {
    const selectedItems = items.filter(item => item.reorderQuantity > 0);
    if (selectedItems.length === 0) {
      toast({
        title: "No items selected",
        description: "Please select at least one item to reorder.",
        variant: "destructive"
      });
      return;
    }

    toast({
      title: "Added to cart!",
      description: `${getSelectedItemsCount()} items added to your cart.`,
    });

    // Navigate to cart or home
    setLocation('/cart');
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
