import { Badge } from "@/components/ui/badge";
import { Wifi, WifiOff, RefreshCw } from "lucide-react";
import { useDataSync } from "@/hooks/useDataSync";

/**
 * Component to show real-time synchronization status
 * Displays across all dashboards to confirm data consistency
 */
export default function SyncStatus() {
  const { isLoading, hasError, stats, queries } = useDataSync();

  const getSyncStatus = () => {
    // Check for critical errors only (not analytics)
    const criticalError = queries.categories.error || queries.menuItems.error || queries.orders.error;

    if (criticalError) return { icon: WifiOff, text: "Sync Error", variant: "destructive" as const };
    if (isLoading) return { icon: RefreshCw, text: "Syncing...", variant: "secondary" as const };
    return { icon: Wifi, text: "Synced", variant: "default" as const };
  };

  const status = getSyncStatus();
  const Icon = status.icon;

  return (
    <div className="flex items-center space-x-2 text-xs">
      <Badge variant={status.variant} className="flex items-center space-x-1">
        <Icon className={`w-3 h-3 ${isLoading ? 'animate-spin' : ''}`} />
        <span>{status.text}</span>
      </Badge>

      {status.variant !== "destructive" && !isLoading && (
        <div className="flex items-center space-x-1 text-muted-foreground">
          <span>{stats.totalMenuItems} items</span>
          <span>â€¢</span>
          <span>{stats.totalCategories} categories</span>
          <span>â€¢</span>
          <span>{stats.totalOrders} orders</span>
        </div>
      )}
    </div>
  );
}

client/src/hooks/useCart.ts.content:
import { useState, useEffect } from 'react';
import { useToast } from '@/hooks/use-toast';

export interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

export function useCart() {
  const [cart, setCart] = useState<CartItem[]>([]);
  const { toast } = useToast();

  // Load cart from localStorage on component mount
  useEffect(() => {
    const savedCart = localStorage.getItem('kit-canteen-cart');
    if (savedCart) {
      try {
        setCart(JSON.parse(savedCart));
      } catch (error) {
        // Failed to parse cart from localStorage - clear corrupted data
        localStorage.removeItem('kit-canteen-cart');
      }
    }
  }, []);

  // Save cart to localStorage whenever cart changes
  useEffect(() => {
    localStorage.setItem('kit-canteen-cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = (item: { id: number; name: string; price: number }) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(cartItem => cartItem.id === item.id);

      if (existingItem) {
        // Update quantity if item already exists
        return prevCart.map(cartItem =>
          cartItem.id === item.id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
      } else {
        // Add new item to cart
        return [...prevCart, { ...item, quantity: 1 }];
      }
    });

    toast({
      title: "Added to Cart",
      description: `${item.name} added to your cart`,
    });
  };

  const removeFromCart = (itemId: number) => {
    setCart(prevCart => prevCart.filter(item => item.id !== itemId));
    toast({
      title: "Item Removed",
      description: "Item has been removed from your cart",
    });
  };

  const updateQuantity = (itemId: number, newQuantity: number) => {
    if (newQuantity <= 0) {
      removeFromCart(itemId);
      return;
    }

    setCart(prevCart =>
      prevCart.map(item =>
        item.id === itemId
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  const decreaseQuantity = (itemId: number) => {
    const item = cart.find(cartItem => cartItem.id === itemId);
    if (item) {
      if (item.quantity > 1) {
        updateQuantity(itemId, item.quantity - 1);
      } else {
        removeFromCart(itemId);
      }
    }
  };

  const getCartQuantity = (itemId: number) => {
    const item = cart.find(cartItem => cartItem.id === itemId);
    return item ? item.quantity : 0;
  };

  const getTotalItems = () => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const clearCart = () => {
    setCart([]);
    localStorage.removeItem('kit-canteen-cart');
    toast({
      title: "Cart Cleared",
      description: "All items have been removed from your cart",
    });
  };

  return {
    cart,
    addToCart,
    removeFromCart,
    updateQuantity,
    decreaseQuantity,
    getCartQuantity,
    getTotalItems,
    getTotalPrice,
    clearCart,
  };
}

client/src/hooks/useDataSync.ts.content:
import { useQuery } from "@tanstack/react-query";
import type { MenuItem, Category, Order, User } from "@shared/schema";

/**
 * Custom hook for synchronized data fetching across all dashboards
 * Ensures real-time data consistency between admin, canteen owner, and student views
 */
export function useDataSync() {
  // Categories query - optimized refresh
  const categoriesQuery = useQuery<Category[]>({
    queryKey: ['/api/categories'],
    staleTime: 1000 * 60 * 10, // 10 minutes (categories change infrequently)
    refetchOnMount: true,
    refetchOnWindowFocus: false, // Categories don't need frequent refetch
  });

  // Menu items query - balanced refresh
  const menuItemsQuery = useQuery<MenuItem[]>({
    queryKey: ['/api/menu'],
    staleTime: 1000 * 60 * 5, // 5 minutes
    refetchOnMount: true,
    refetchOnWindowFocus: true,
  });

  // Orders query - more frequent for real-time updates
  const ordersQuery = useQuery<Order[]>({
    queryKey: ['/api/orders'],
    staleTime: 1000 * 60, // 1 minute for orders
    refetchOnMount: true,
    refetchOnWindowFocus: false, // Disable window focus refetch
    refetchInterval: false, // Disable polling - using SSE for real-time updates
  });

  // Analytics query - least frequent
  const analyticsQuery = useQuery({
    queryKey: ['/api/admin/analytics'],
    staleTime: 1000 * 60 * 15, // 15 minutes for analytics
    refetchOnMount: true,
    refetchOnWindowFocus: false,
    retry: false, // Don't retry analytics failures
    retryOnMount: false,
  });

  // Computed values for dashboard consistency
  const stats = {
    totalCategories: categoriesQuery.data?.length || 0,
    totalMenuItems: menuItemsQuery.data?.length || 0,
    availableItems: menuItemsQuery.data?.filter(item => item.available).length || 0,
    totalOrders: ordersQuery.data?.length || 0,
    pendingOrders: ordersQuery.data?.filter(order => order.status === 'preparing').length || 0,
    completedOrders: ordersQuery.data?.filter(order => order.status === 'completed').length || 0,
    totalRevenue: ordersQuery.data?.reduce((sum, order) => sum + order.amount, 0) || 0,
  };

  // Combined loading state
  const isLoading = categoriesQuery.isLoading || menuItemsQuery.isLoading || ordersQuery.isLoading;

  // Combined error state (excluding analytics errors as they're optional)
  const hasError = categoriesQuery.error || menuItemsQuery.error || ordersQuery.error;

  return {
    // Raw data
    categories: categoriesQuery.data || [],
    menuItems: menuItemsQuery.data || [],
    orders: ordersQuery.data || [],
    analytics: analyticsQuery.data,

    // Computed stats
    stats,

    // Loading and error states
    isLoading,
    hasError,

    // Refetch functions for manual sync
    refetch: {
      categories: categoriesQuery.refetch,
      menuItems: menuItemsQuery.refetch,
      orders: ordersQuery.refetch,
      analytics: analyticsQuery.refetch,
      all: () => {
        categoriesQuery.refetch();
        menuItemsQuery.refetch();
        ordersQuery.refetch();
        analyticsQuery.refetch();
      }
    },

    // Individual query states for granular control
    queries: {
      categories: categoriesQuery,
      menuItems: menuItemsQuery,
      orders: ordersQuery,
      analytics: analyticsQuery,
    }
  };
}

/**
 * Hook specifically for authentication state synchronization
 */
export function useAuthSync() {
  const user = JSON.parse(localStorage.getItem('user') || 'null');

  // Debug logging for authentication state
  console.log("useAuthSync Debug:", {
    localStorage_user: localStorage.getItem('user'),
    parsed_user: user,
    isAuthenticated: !!user,
    userRole: user?.role
  });

  return {
    user,
    isAuthenticated: !!user,
    isAdmin: user?.role === 'admin' || user?.role === 'super_admin',
    isSuperAdmin: user?.role === 'super_admin',
    isCanteenOwner: user?.role === 'canteen_owner',
    isStudent: user?.role === 'student',
    isStaff: user?.role === 'staff',
    hasRole: (role: string) => user?.role === role,
  };
}

client/src/components/BarcodeDisplay.tsx.content:
import { useEffect, useRef } from 'react';
import JsBarcode from 'jsbarcode';

interface BarcodeDisplayProps {
  value: string;
  width?: number;
  height?: number;
  displayValue?: boolean;
  className?: string;
}

export default function BarcodeDisplay({ 
  value, 
  width = 2, 
  height = 100, 
  displayValue = true,
  className = "" 
}: BarcodeDisplayProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    if (canvasRef.current && value) {
      try {
        JsBarcode(canvasRef.current, value, {
          format: "CODE128",
          width: width,
          height: height,
          displayValue: displayValue,
          fontSize: 14,
          textAlign: "center",
          textPosition: "bottom",
          background: "#ffffff",
          lineColor: "#000000",
          margin: 10
        });
      } catch (error) {
        console.error('Error generating barcode:', error);
      }
    }
  }, [value, width, height, displayValue]);

  if (!value) {
    return (
      <div className={`flex items-center justify-center p-4 border rounded ${className}`}>
        <span className="text-muted-foreground">No barcode available</span>
      </div>
    );
  }

  return (
    <div className={`flex flex-col items-center space-y-2 ${className}`}>
      <canvas ref={canvasRef} className="border rounded" />
      {displayValue && (
        <span className="text-xs text-muted-foreground font-mono">{value}</span>
      )}
    </div>
  );
}

client/src/components/ProtectedRoute.tsx.content:
import { useEffect } from "react";
import { useLocation } from "wouter";
import { useAuthSync } from "@/hooks/useDataSync";
import { Button } from "@/components/ui/button";
import { toast } from "sonner";

interface ProtectedRouteProps {
  children: React.ReactNode;
  requiredRole?: string;
  requiredRoles?: string[];
  requireAuth?: boolean;
}

export default function ProtectedRoute({ 
  children, 
  requiredRole, 
  requiredRoles = [], 
  requireAuth = true 
}: ProtectedRouteProps) {
  const [, setLocation] = useLocation();
  const { user, isAuthenticated, hasRole } = useAuthSync();

  // Authentication and role checking logic

  useEffect(() => {
    // Check if authentication is required
    if (requireAuth && !isAuthenticated) {
      toast("Please log in to access this page.");
      setLocation("/login");
      return;
    }

    // Check specific role requirements
    if (requiredRole && !hasRole(requiredRole)) {
      toast("Access denied. Insufficient permissions.");
      setLocation("/login");
      return;
    }

    // Check multiple role requirements
    if (requiredRoles.length > 0 && !requiredRoles.some(role => hasRole(role))) {
      toast("Access denied. Insufficient permissions.");
      setLocation("/login");
      return;
    }
  }, [isAuthenticated, user, requiredRole, requiredRoles, requireAuth, hasRole, setLocation]);

  // Return access denied screen immediately if not authorized
  if (requireAuth && !isAuthenticated) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-4">Authentication Required</h2>
          <p className="text-muted-foreground mb-4">Please log in to access this page</p>
          <Button onClick={() => setLocation("/login")}>Go to Login</Button>
        </div>
      </div>
    );
  }

  if (requiredRole && !hasRole(requiredRole)) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-4">Access Denied</h2>
          <p className="text-muted-foreground mb-4">You don't have permission to access this page</p>
          <Button onClick={() => setLocation("/login")}>Go to Login</Button>
        </div>
      </div>
    );
  }

  if (requiredRoles.length > 0 && !requiredRoles.some(role => hasRole(role))) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-xl font-semibold mb-4">Access Denied</h2>
          <p className="text-muted-foreground mb-4">You don't have permission to access this page</p>
          <Button onClick={() => setLocation("/login")}>Go to Login</Button>
        </div>
      </div>
    );
  }

  return <>{children}</>;
}

