                          <span>Department:</span>
                          <div className="flex items-center">
                            {departmentValidation.isValid ? (
                              <><Check className="w-3 h-3 text-green-600 mr-1" /> Selected</>
                            ) : (
                              <span className="text-gray-500">Pending</span>
