import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, AlertCircle, CheckCircle, Send, Phone, Mail } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

const loginIssueSchema = z.object({
  name: z.string().min(1, "Name is required"),
  email: z.string().email("Valid email is required").optional().or(z.literal("")),
  phoneNumber: z.string().min(10, "Valid phone number is required").optional().or(z.literal("")),
  registerNumber: z.string().optional().or(z.literal("")),
  staffId: z.string().optional().or(z.literal("")),
  userType: z.enum(["student", "staff"]),
  issueType: z.enum([
    "forgot_email",
    "account_locked", 
    "email_changed",
    "registration_problem",
    "other"
  ]),
  description: z.string().min(10, "Please provide a detailed description (at least 10 characters)"),
});

type LoginIssueForm = z.infer<typeof loginIssueSchema>;

interface LoginIssuesScreenProps {
  onBackToLogin: () => void;
}

export default function LoginIssuesScreen({ onBackToLogin }: LoginIssuesScreenProps) {
  const { toast } = useToast();
  const [isSubmitted, setIsSubmitted] = useState(false);

  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors, isValid },
  } = useForm<LoginIssueForm>({
    resolver: zodResolver(loginIssueSchema),
    defaultValues: {
      userType: "student",
      issueType: "forgot_email",
    },
  });

  const userType = watch("userType");
  const issueType = watch("issueType");

  const submitIssueMutation = useMutation({
    mutationFn: async (data: LoginIssueForm) => {
      // Remove userType from the data as it's not part of the schema
      const { userType, ...issueData } = data;
      return apiRequest("/api/login-issues", {
        method: "POST",
        body: JSON.stringify(issueData),
      });
    },
    onSuccess: () => {
      setIsSubmitted(true);
      toast({
        title: "Issue Reported Successfully",
        description: "We've received your report and will get back to you soon.",
      });
    },
    onError: () => {
      toast({
        title: "Submission Failed",
        description: "Unable to submit your report. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: LoginIssueForm) => {
    submitIssueMutation.mutate(data);
  };

  const issueTypeOptions = [
    { value: "forgot_email", label: "Forgot Email Address", description: "Can't remember which email I used to register" },
    { value: "account_locked", label: "Account Locked", description: "My account seems to be locked or suspended" },
    { value: "email_changed", label: "Email Changed", description: "I changed my email and can't sign in" },
    { value: "registration_problem", label: "Registration Problem", description: "Having trouble during registration process" },
    { value: "other", label: "Other Issue", description: "Different login-related problem" },
  ];

  if (isSubmitted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-orange-50 to-red-50 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardContent className="p-8 text-center">
            <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-6" />
            <h2 className="text-2xl font-bold mb-4">Report Submitted!</h2>
            <p className="text-muted-foreground mb-6">
              Thank you for reporting this issue. Our admin team will review your request and contact you soon.
            </p>
            <div className="space-y-3">
              <p className="text-sm text-muted-foreground">
                You should receive a response within 24-48 hours.
              </p>
              <Button onClick={onBackToLogin} className="w-full">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Login
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-red-50 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
      <Card className="w-full max-w-2xl">
        <CardHeader className="text-center">
          <div className="flex items-center justify-center mb-4">
            <AlertCircle className="w-12 h-12 text-orange-500" />
          </div>
          <CardTitle className="text-2xl">Report Login Issue</CardTitle>
          <p className="text-muted-foreground">
            Having trouble logging in? Tell us about it and we'll help you resolve the issue.
          </p>
        </CardHeader>

        <CardContent className="space-y-6">
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
            {/* Basic Information */}
            <div className="space-y-4">
              <div>
                <Label htmlFor="name">Full Name *</Label>
                <Input
                  id="name"
                  {...register("name")}
                  placeholder="Enter your full name"
                  className={errors.name ? "border-red-500" : ""}
                />
                {errors.name && (
                  <p className="text-sm text-red-500 mt-1">{errors.name.message}</p>
                )}
              </div>

              <div>
                <Label>I am a *</Label>
                <RadioGroup
                  value={userType}
                  onValueChange={(value) => setValue("userType", value as "student" | "staff")}
                  className="flex space-x-6 mt-2"
                >
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="student" id="student" />
                    <Label htmlFor="student">Student</Label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <RadioGroupItem value="staff" id="staff" />
                    <Label htmlFor="staff">Staff Member</Label>
                  </div>
                </RadioGroup>
              </div>

              {userType === "student" ? (
                <div>
                  <Label htmlFor="registerNumber">Register Number</Label>
                  <Input
                    id="registerNumber"
                    {...register("registerNumber")}
                    placeholder="Enter your register number (e.g., 21CS001)"
                  />
                  {errors.registerNumber && (
                    <p className="text-sm text-red-500 mt-1">{errors.registerNumber.message}</p>
                  )}
                </div>
              ) : (
                <div>
                  <Label htmlFor="staffId">Staff ID</Label>
                  <Input
                    id="staffId"
                    {...register("staffId")}
                    placeholder="Enter your staff ID (e.g., 123456)"
                  />
                  {errors.staffId && (
                    <p className="text-sm text-red-500 mt-1">{errors.staffId.message}</p>
                  )}
                </div>
              )}
            </div>

            {/* Contact Information */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Contact Information</h3>

              <div>
                <Label htmlFor="email">Email Address</Label>
                <div className="relative">
                  <Mail className="w-4 h-4 absolute left-3 top-3 text-muted-foreground" />
                  <Input
                    id="email"
                    {...register("email")}
                    type="email"
                    placeholder="your.email@example.com (optional)"
                    className={`pl-10 ${errors.email ? "border-red-500" : ""}`}
                  />
                </div>
                {errors.email && (
                  <p className="text-sm text-red-500 mt-1">{errors.email.message}</p>
                )}
              </div>

              <div>
                <Label htmlFor="phoneNumber">Phone Number</Label>
                <div className="relative">
                  <Phone className="w-4 h-4 absolute left-3 top-3 text-muted-foreground" />
                  <Input
                    id="phoneNumber"
                    {...register("phoneNumber")}
                    type="tel"
                    placeholder="Your phone number (optional)"
                    className={`pl-10 ${errors.phoneNumber ? "border-red-500" : ""}`}
                  />
                </div>
                {errors.phoneNumber && (
                  <p className="text-sm text-red-500 mt-1">{errors.phoneNumber.message}</p>
                )}
              </div>
            </div>

            {/* Issue Details */}
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Issue Details</h3>

              <div>
                <Label htmlFor="issueType">Type of Issue *</Label>
                <Select
                  value={issueType}
                  onValueChange={(value) => setValue("issueType", value as any)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select the type of issue" />
                  </SelectTrigger>
                  <SelectContent>
                    {issueTypeOptions.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        <div>
                          <div className="font-medium">{option.label}</div>
                          <div className="text-sm text-muted-foreground">{option.description}</div>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label htmlFor="description">Detailed Description *</Label>
                <Textarea
                  id="description"
                  {...register("description")}
                  placeholder="Please describe your issue in detail. Include any error messages you've seen, steps you've tried, and when the problem started."
                  rows={4}
                  className={errors.description ? "border-red-500" : ""}
                />
                {errors.description && (
                  <p className="text-sm text-red-500 mt-1">{errors.description.message}</p>
                )}
              </div>
            </div>

            {/* Submit Section */}
            <div className="flex flex-col space-y-4 pt-4">
              <Button 
                type="submit" 
                disabled={!isValid || submitIssueMutation.isPending}
                className="w-full"
              >
                <Send className="w-4 h-4 mr-2" />
                {submitIssueMutation.isPending ? "Submitting..." : "Submit Report"}
              </Button>

              <Button variant="outline" onClick={onBackToLogin} className="w-full">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Login
              </Button>
            </div>
          </form>

          {/* Help Text */}
          <div className="bg-blue-50 dark:bg-blue-900/20 p-4 rounded-lg">
            <h4 className="font-semibold text-blue-900 dark:text-blue-100 mb-2">
              Need Immediate Help?
            </h4>
            <p className="text-sm text-blue-700 dark:text-blue-200">
              For urgent issues, you can also contact the admin directly at the college IT help desk 
              or visit the admin office during working hours (9 AM - 5 PM).
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/AdminLoginIssues.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { Eye, MessageSquare, CheckCircle, Clock, AlertTriangle, RefreshCw } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface LoginIssue {
  id: number;
  name: string;
  email?: string;
  phoneNumber?: string;
  registerNumber?: string;
  staffId?: string;
  issueType: string;
  description: string;
  status: "pending" | "in_progress" | "resolved";
  adminNotes?: string;
  resolvedBy?: string;
  resolvedAt?: string;
  createdAt: string;
}

export default function AdminLoginIssues() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedIssue, setSelectedIssue] = useState<LoginIssue | null>(null);
  const [adminNotes, setAdminNotes] = useState("");
  const [newStatus, setNewStatus] = useState<string>("");
  const [resolvedBy, setResolvedBy] = useState("");

  const { data: issues = [], isLoading, error } = useQuery<LoginIssue[]>({
    queryKey: ["/api/login-issues"],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  const updateIssueMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: number; updates: any }) => {
      return apiRequest(`/api/login-issues/${id}`, {
        method: "PATCH",
        body: JSON.stringify(updates),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/login-issues"] });
      toast({
        title: "Issue Updated",
        description: "Login issue has been updated successfully.",
      });
      setSelectedIssue(null);
      setAdminNotes("");
      setNewStatus("");
      setResolvedBy("");
    },
    onError: () => {
      toast({
        title: "Update Failed",
        description: "Unable to update the issue. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteIssueMutation = useMutation({
    mutationFn: async (id: number) => {
      return apiRequest(`/api/login-issues/${id}`, {
        method: "DELETE",
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/login-issues"] });
      toast({
        title: "Issue Deleted",
        description: "Login issue has been deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Delete Failed",
        description: "Unable to delete the issue. Please try again.",
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "pending":
        return <Badge variant="destructive"><Clock className="w-3 h-3 mr-1" />Pending</Badge>;
      case "in_progress":
        return <Badge variant="default"><RefreshCw className="w-3 h-3 mr-1" />In Progress</Badge>;
      case "resolved":
        return <Badge variant="secondary"><CheckCircle className="w-3 h-3 mr-1" />Resolved</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const getIssueTypeBadge = (type: string) => {
    const typeMap: { [key: string]: { label: string; variant: any } } = {
      forgot_email: { label: "Forgot Email", variant: "default" },
      account_locked: { label: "Account Locked", variant: "destructive" },
      email_changed: { label: "Email Changed", variant: "secondary" },
      registration_problem: { label: "Registration Problem", variant: "outline" },
      other: { label: "Other", variant: "outline" },
    };

    const config = typeMap[type] || { label: type, variant: "outline" };
    return <Badge variant={config.variant}>{config.label}</Badge>;
  };

  const handleUpdateIssue = () => {
    if (!selectedIssue) return;

    const updates: any = {};
    if (newStatus) {
      updates.status = newStatus;
      if (newStatus === "resolved" && resolvedBy) {
        updates.resolvedBy = resolvedBy;
      }
    }
    if (adminNotes !== selectedIssue.adminNotes) {
      updates.adminNotes = adminNotes;
    }

    updateIssueMutation.mutate({ id: selectedIssue.id, updates });
  };

  const openIssueDialog = (issue: LoginIssue) => {
    setSelectedIssue(issue);
    setAdminNotes(issue.adminNotes || "");
    setNewStatus(issue.status);
    setResolvedBy(issue.resolvedBy || "");
  };

  const pendingCount = issues.filter((issue) => issue.status === "pending").length;
  const inProgressCount = issues.filter((issue) => issue.status === "in_progress").length;
  const resolvedCount = issues.filter((issue) => issue.status === "resolved").length;

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="flex justify-center items-center py-12">
          <RefreshCw className="w-6 h-6 animate-spin mr-2" />
          Loading login issues...
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6">
        <Card>
          <CardContent className="p-6 text-center">
            <AlertTriangle className="w-12 h-12 text-red-500 mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">Error Loading Issues</h3>
            <p className="text-muted-foreground">Unable to load login issues. Please refresh the page.</p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Login Issues Management</h1>
          <p className="text-muted-foreground">Monitor and resolve user login problems</p>
        </div>
        <Button 
          onClick={() => queryClient.invalidateQueries({ queryKey: ["/api/login-issues"] })}
          variant="outline"
        >
          <RefreshCw className="w-4 h-4 mr-2" />
          Refresh
        </Button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Total Issues</p>
                <p className="text-2xl font-bold">{issues.length}</p>
              </div>
              <AlertTriangle className="w-8 h-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Pending</p>
                <p className="text-2xl font-bold text-red-600">{pendingCount}</p>
              </div>
              <Clock className="w-8 h-8 text-red-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">In Progress</p>
                <p className="text-2xl font-bold text-yellow-600">{inProgressCount}</p>
              </div>
              <RefreshCw className="w-8 h-8 text-yellow-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Resolved</p>
                <p className="text-2xl font-bold text-green-600">{resolvedCount}</p>
              </div>
              <CheckCircle className="w-8 h-8 text-green-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Issues List */}
      <Card>
        <CardHeader>
          <CardTitle>Login Issues</CardTitle>
        </CardHeader>
        <CardContent>
          {issues.length === 0 ? (
            <div className="text-center py-12">
              <CheckCircle className="w-12 h-12 text-green-500 mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">No Login Issues</h3>
              <p className="text-muted-foreground">Great! There are currently no reported login issues.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {issues.map((issue) => (
                <div key={issue.id} className="border rounded-lg p-4 space-y-3">
                  <div className="flex justify-between items-start">
                    <div className="space-y-1">
                      <div className="flex items-center gap-2">
                        <h3 className="font-semibold">{issue.name}</h3>
                        {getStatusBadge(issue.status)}
                        {getIssueTypeBadge(issue.issueType)}
                      </div>
                      <div className="text-sm text-muted-foreground space-y-1">
                        {issue.registerNumber && <p>Register: {issue.registerNumber}</p>}
                        {issue.staffId && <p>Staff ID: {issue.staffId}</p>}
                        {issue.email && <p>Email: {issue.email}</p>}
                        {issue.phoneNumber && <p>Phone: {issue.phoneNumber}</p>}
                        <p>Reported: {new Date(issue.createdAt).toLocaleString()}</p>
                        {issue.resolvedAt && <p>Resolved: {new Date(issue.resolvedAt).toLocaleString()}</p>}
                        {issue.resolvedBy && <p>Resolved by: {issue.resolvedBy}</p>}
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <Dialog>
                        <DialogTrigger asChild>
                          <Button 
                            variant="outline" 
                            size="sm"
                            onClick={() => openIssueDialog(issue)}
                          >
                            <Eye className="w-4 h-4 mr-1" />
                            View
                          </Button>
                        </DialogTrigger>
                        <DialogContent className="max-w-2xl">
                          <DialogHeader>
                            <DialogTitle>Login Issue Details</DialogTitle>
                          </DialogHeader>

                          {selectedIssue && (
                            <div className="space-y-6">
                              {/* Issue Details */}
                              <div className="space-y-4">
                                <div className="grid grid-cols-2 gap-4">
                                  <div>
                                    <Label>Name</Label>
                                    <p className="font-medium">{selectedIssue.name}</p>
                                  </div>
                                  <div>
                                    <Label>Issue Type</Label>
                                    <p>{getIssueTypeBadge(selectedIssue.issueType)}</p>
                                  </div>
                                  <div>
                                    <Label>Status</Label>
                                    <p>{getStatusBadge(selectedIssue.status)}</p>
                                  </div>
                                  <div>
                                    <Label>Reported</Label>
                                    <p>{new Date(selectedIssue.createdAt).toLocaleString()}</p>
                                  </div>
                                </div>

                                <div>
                                  <Label>Description</Label>
                                  <div className="mt-1 p-3 bg-muted rounded-md">
                                    <p className="whitespace-pre-wrap">{selectedIssue.description}</p>
                                  </div>
                                </div>

                                <div className="grid grid-cols-2 gap-4">
                                  {selectedIssue.email && (
                                    <div>
                                      <Label>Email</Label>
                                      <p>{selectedIssue.email}</p>
                                    </div>
                                  )}
                                  {selectedIssue.phoneNumber && (
                                    <div>
                                      <Label>Phone</Label>
                                      <p>{selectedIssue.phoneNumber}</p>
                                    </div>
                                  )}
                                  {selectedIssue.registerNumber && (
                                    <div>
                                      <Label>Register Number</Label>
                                      <p>{selectedIssue.registerNumber}</p>
                                    </div>
                                  )}
                                  {selectedIssue.staffId && (
                                    <div>
                                      <Label>Staff ID</Label>
                                      <p>{selectedIssue.staffId}</p>
                                    </div>
                                  )}
                                </div>
                              </div>

                              {/* Admin Actions */}
                              <div className="border-t pt-4 space-y-4">
                                <h4 className="font-semibold">Admin Actions</h4>

                                <div className="grid grid-cols-2 gap-4">
                                  <div>
                                    <Label>Update Status</Label>
                                    <Select value={newStatus} onValueChange={setNewStatus}>
                                      <SelectTrigger>
                                        <SelectValue />
                                      </SelectTrigger>
                                      <SelectContent>
                                        <SelectItem value="pending">Pending</SelectItem>
                                        <SelectItem value="in_progress">In Progress</SelectItem>
                                        <SelectItem value="resolved">Resolved</SelectItem>
                                      </SelectContent>
                                    </Select>
                                  </div>

                                  {newStatus === "resolved" && (
                                    <div>
                                      <Label>Resolved By</Label>
                                      <Input
                                        value={resolvedBy}
                                        onChange={(e) => setResolvedBy(e.target.value)}
                                        placeholder="Enter admin name"
                                      />
                                    </div>
                                  )}
                                </div>

                                <div>
                                  <Label>Admin Notes</Label>
                                  <Textarea
                                    value={adminNotes}
                                    onChange={(e) => setAdminNotes(e.target.value)}
                                    placeholder="Add notes about resolution steps, communication with user, etc."
                                    rows={4}
                                  />
                                </div>

                                <div className="flex gap-3 pt-4">
                                  <Button 
                                    onClick={handleUpdateIssue}
                                    disabled={updateIssueMutation.isPending}
                                    className="flex-1"
                                  >
                                    <MessageSquare className="w-4 h-4 mr-2" />
                                    {updateIssueMutation.isPending ? "Updating..." : "Update Issue"}
                                  </Button>
                                  <Button
                                    variant="destructive"
                                    onClick={() => {
                                      if (confirm("Are you sure you want to delete this issue?")) {
                                        deleteIssueMutation.mutate(selectedIssue.id);
                                        setSelectedIssue(null);
                                      }
                                    }}
                                    disabled={deleteIssueMutation.isPending}
                                  >
                                    Delete
                                  </Button>
                                </div>
                              </div>
                            </div>
                          )}
                        </DialogContent>
                      </Dialog>
                    </div>
                  </div>

                  <div>
                    <p className="text-sm line-clamp-2">{issue.description}</p>
                  </div>

                  {issue.adminNotes && (
                    <div className="border-t pt-2">
                      <p className="text-xs text-muted-foreground">Admin Notes:</p>
                      <p className="text-sm line-clamp-1">{issue.adminNotes}</p>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

