import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Switch } from "@/components/ui/switch";
import { Plus, Flame, Star } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { VegIndicator } from "@/components/ui/VegIndicator";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { MenuItem } from "@shared/schema";

export function TrendingItemsManager() {
  const { toast } = useToast();

  const { data: menuItems = [], isLoading: menuItemsLoading } = useQuery<MenuItem[]>({
    queryKey: ["/api/menu"],
    staleTime: 1000 * 30,
    refetchOnMount: true,
  });

  const updateMenuItemMutation = useMutation({
    mutationFn: async ({ id, data }: { id: number; data: Partial<MenuItem> }) => {
      return apiRequest(`/api/menu/${id}`, {
        method: "PUT",
        body: JSON.stringify(data),
        headers: { "Content-Type": "application/json" },
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/menu"] });
      toast({
        title: "Trending status updated",
        description: "The menu item's trending status has been updated.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update trending status. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleToggleTrending = (menuItem: MenuItem) => {
    updateMenuItemMutation.mutate({
      id: menuItem.id,
      data: { isTrending: !menuItem.isTrending },
    });
  };

  const trendingItems = menuItems.filter(item => item.isTrending);
  const availableItems = menuItems.filter(item => item.available);

  if (menuItemsLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Flame className="w-5 h-5 text-orange-500" />
            Trending Items Configuration
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-center py-8">
            <div className="text-muted-foreground">Loading menu items...</div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Flame className="w-5 h-5 text-orange-500" />
          Trending Items Configuration
        </CardTitle>
        <p className="text-sm text-muted-foreground">
          Configure which menu items appear in the "Trending Now" section on users' home page.
          Simply toggle the trending status for any menu item.
        </p>
      </CardHeader>
      <CardContent className="space-y-6">
        <div className="flex justify-between items-center p-4 bg-orange-50 dark:bg-orange-950/20 rounded-lg border">
          <div className="flex items-center gap-2">
            <Star className="w-5 h-5 text-orange-600" />
            <div>
              <div className="font-medium">Currently Trending</div>
              <div className="text-sm text-muted-foreground">
                {trendingItems.length} items marked as trending
              </div>
            </div>
          </div>
          <Badge variant="secondary" className="bg-orange-100 text-orange-700">
            {trendingItems.length} trending
          </Badge>
        </div>

        <div className="space-y-4">
          <h3 className="font-medium">All Menu Items</h3>
          {availableItems.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No menu items available. Add some menu items first.
            </div>
          ) : (
            <div className="grid gap-3">
              {availableItems.map((item) => (
                <Card key={item.id} className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <VegIndicator isVegetarian={item.isVegetarian} size="sm" />
                      <div className="flex-1">
                        <div className="flex items-center gap-2">
                          <div className="font-medium">{item.name}</div>
                          {item.isTrending && (
                            <Badge variant="secondary" className="bg-orange-100 text-orange-700 text-xs">
                              <Flame className="w-3 h-3 mr-1" />
                              Trending
                            </Badge>
                          )}
                        </div>
                        <div className="text-sm text-muted-foreground">
                          â‚¹{item.price} â€¢ Stock: {item.stock}
                        </div>
                        {item.description && (
                          <div className="text-xs text-muted-foreground mt-1">
                            {item.description}
                          </div>
                        )}
                      </div>
                    </div>
                    <div className="flex items-center gap-3">
                      <div className="text-sm text-muted-foreground">
                        {item.isTrending ? "Trending" : "Not trending"}
                      </div>
                      <Switch
                        checked={item.isTrending}
                        onCheckedChange={() => handleToggleTrending(item)}
                        disabled={updateMenuItemMutation.isPending}
                      />
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>

        {trendingItems.length > 0 && (
          <div className="space-y-4">
            <h3 className="font-medium">Preview: Trending Items</h3>
            <div className="p-4 bg-gray-50 dark:bg-gray-900 rounded-lg">
              <div className="text-sm text-muted-foreground mb-3">
                This is how these items will appear in the "Trending Now" section:
              </div>
              <div className="grid gap-3">
                {trendingItems.map((item) => (
                  <div key={item.id} className="flex items-center gap-3 p-3 bg-white dark:bg-gray-800 rounded-lg border">
                    <div className="w-12 h-12 bg-gradient-to-br from-orange-400 to-red-500 rounded-lg flex-shrink-0 flex items-center justify-center">
                      <Flame className="w-6 h-6 text-white" />
                    </div>
                    <div className="flex-1">
                      <div className="flex items-center gap-2">
                        <div className="font-medium">{item.name}</div>
                        <VegIndicator isVegetarian={item.isVegetarian} size="sm" />
                        <Badge variant="secondary" className="bg-orange-100 text-orange-700 text-xs">
                          ðŸ”¥ Trending
                        </Badge>
                      </div>
                      <div className="text-sm text-muted-foreground">Available now</div>
                    </div>
                    <div className="text-right">
                      <p className="font-bold">â‚¹{item.price}</p>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

shared/phonepe.ts.content:
import crypto from 'crypto';
import axios from 'axios';

// PhonePe OAuth Configuration - loaded from environment variables
export const PHONEPE_CONFIG = {
  CLIENT_ID: process.env.PHONEPE_CLIENT_ID || '',
  CLIENT_SECRET: process.env.PHONEPE_CLIENT_SECRET || '',
  CLIENT_VERSION: process.env.PHONEPE_CLIENT_VERSION || '1',
  MERCHANT_ID: process.env.PHONEPE_MERCHANT_ID || '', // Still needed for some requests

  // OAuth and API URLs
  AUTH_BASE_URL: process.env.PHONEPE_AUTH_BASE_URL || 'https://api.phonepe.com/apis/identity-manager/v1',
  API_BASE_URL: process.env.PHONEPE_API_BASE_URL || 'https://api.phonepe.com/apis/pg/checkout/v2',

  // Legacy support (keeping for backward compatibility)
  SALT_KEY: process.env.PHONEPE_SALT_KEY || '',
  SALT_INDEX: process.env.PHONEPE_SALT_INDEX || '1',
  CALLBACK_VERSION: '/pg/v1/callback'
};

// OAuth token cache
let tokenCache: {
  token: string;
  expiresAt: number;
} | null = null;

// Generate OAuth access token
export async function getOAuthToken(): Promise<string> {
  // Check if we have a valid cached token
  if (tokenCache && tokenCache.expiresAt > Date.now()) {
    return tokenCache.token;
  }

  try {
    console.log('ðŸ”‘ Generating new PhonePe OAuth token...');

    const tokenResponse = await axios.post(
      `${PHONEPE_CONFIG.AUTH_BASE_URL}/oauth/token`,
      new URLSearchParams({
        client_id: PHONEPE_CONFIG.CLIENT_ID,
        client_secret: PHONEPE_CONFIG.CLIENT_SECRET,
        client_version: PHONEPE_CONFIG.CLIENT_VERSION,
        grant_type: 'client_credentials'
      }).toString(),
      {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        },
        timeout: 10000
      }
    );

    if (tokenResponse.data.access_token) {
      // Cache the token with expiry (subtract 5 minutes for safety)
      const expiresAt = tokenResponse.data.expires_at ? 
        (tokenResponse.data.expires_at * 1000) - (5 * 60 * 1000) : 
        Date.now() + (55 * 60 * 1000); // Default 55 minutes

      tokenCache = {
        token: tokenResponse.data.access_token,
        expiresAt
      };

      console.log('âœ… PhonePe OAuth token generated successfully');
      return tokenResponse.data.access_token;
    } else {
      throw new Error('No access token in response');
    }
  } catch (error) {
    console.error('âŒ OAuth token generation failed:', error);
    throw new Error(`OAuth token generation failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

// Legacy checksum function (kept for backward compatibility)
export function generatePaymentChecksum(payload: any, endpoint: string): string {
  const base64Payload = Buffer.from(JSON.stringify(payload)).toString('base64');
  const string = base64Payload + endpoint + PHONEPE_CONFIG.SALT_KEY;
  const sha256 = crypto.createHash('sha256').update(string).digest('hex');
  return sha256 + '###' + PHONEPE_CONFIG.SALT_INDEX;
}

// Generate checksum for status verification
export function generateStatusChecksum(merchantTransactionId: string): string {
  const endpoint = `/pg/v1/status/${PHONEPE_CONFIG.MERCHANT_ID}/${merchantTransactionId}`;
  const string = endpoint + PHONEPE_CONFIG.SALT_KEY;
  const sha256 = crypto.createHash('sha256').update(string).digest('hex');
  return sha256 + '###' + PHONEPE_CONFIG.SALT_INDEX;
}

// Verify webhook checksum
export function verifyWebhookChecksum(payload: any, receivedChecksum: string): boolean {
  const payloadString = JSON.stringify(payload);
  const string = payloadString + PHONEPE_CONFIG.CALLBACK_VERSION + PHONEPE_CONFIG.SALT_KEY;
  const expectedChecksum = crypto.createHash('sha256').update(string).digest('hex') + '###' + PHONEPE_CONFIG.SALT_INDEX;
  return receivedChecksum === expectedChecksum;
}

// Create OAuth payment payload (v2 API format)
export function createOAuthPaymentPayload(
  merchantOrderId: string,
  amount: number,
  redirectUrl: string
) {
  return {
    merchantOrderId,
    amount, // Amount in paisa
    paymentFlow: {
      type: 'PG_CHECKOUT',
      merchantUrls: {
        redirectUrl
      }
    }
  };
}

// Legacy payment payload (kept for backward compatibility)
export function createPaymentPayload(
  merchantTransactionId: string,
  amount: number,
  customerName: string,
  redirectUrl: string,
  callbackUrl: string
) {
  return {
    merchantId: PHONEPE_CONFIG.MERCHANT_ID,
    merchantTransactionId,
    merchantUserId: `USER_${Date.now()}`,
    amount: amount * 100, // Convert to paise
    redirectUrl,
    callbackUrl,
    paymentInstrument: {
      type: 'PAY_PAGE'
    }
  };
}

// Payment status codes
export const PAYMENT_STATUS = {
  PENDING: 'pending',
  SUCCESS: 'success', 
  FAILED: 'failed',
  TIMEOUT: 'timeout'
} as const;

// Response codes from PhonePe
export const PHONEPE_RESPONSE_CODES = {
  PAYMENT_SUCCESS: 'PAYMENT_SUCCESS',
  PAYMENT_FAILED: 'PAYMENT_FAILED', 
  PAYMENT_PENDING: 'PAYMENT_PENDING',
  PAYMENT_INITIATED: 'PAYMENT_INITIATED',
  BAD_REQUEST: 'BAD_REQUEST',
  AUTHORIZATION_FAILED: 'AUTHORIZATION_FAILED',
  INTERNAL_SERVER_ERROR: 'INTERNAL_SERVER_ERROR',
  TRANSACTION_TIMEOUT: 'TRANSACTION_TIMEOUT'
} as const;

