              <div className="bg-blue-50 rounded-lg p-4">
                <h3 className="font-bold text-xl mb-4 flex items-center text-gray-800">
                  <ChefHat className="w-6 h-6 mr-2 text-blue-600" />
                  Ordered Dishes
                </h3>
                <div className="space-y-3">
                  {(() => {
                    try {
                      const items = typeof selectedOrderForDetails.items === 'string' 
                        ? JSON.parse(selectedOrderForDetails.items) 
                        : selectedOrderForDetails.items || [];

                      return items.length > 0 ? items.map((item: any, index: number) => (
                        <div key={index} className="bg-white rounded-lg p-4 shadow-sm border border-blue-200">
                          <div className="flex justify-between items-start">
                            <div className="flex-1">
                              <div className="flex items-center space-x-3 mb-2">
                                <span className="font-bold text-xl text-gray-800">{item.name}</span>
                                {item.isVegetarian && (
                                  <span className="bg-green-500 text-white px-3 py-1 rounded-full text-sm font-semibold">VEG</span>
                                )}
                              </div>
                              <div className="text-gray-600 font-medium text-lg">
                                â‚¹{item.price} Ã— {item.quantity} pieces
                              </div>
                            </div>
                            <div className="text-right">
                              <div className="font-bold text-2xl text-blue-600">â‚¹{item.price * item.quantity}</div>
                            </div>
                          </div>
                        </div>
                      )) : (
                        <div className="text-center py-4 text-muted-foreground">No items found</div>
                      );
                    } catch (error) {
                      return (
                        <div className="text-center py-4 text-red-500">Error loading items</div>
                      );
                    }
                  })()}
                </div>
              </div>

              {/* Order Information */}
              <div className="grid grid-cols-2 gap-4">
                <div className="bg-gray-50 rounded-lg p-4">
                  <h4 className="font-semibold mb-2">Order Information</h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Order ID:</span>
                      <span className="font-mono">#{(() => {
                        const formatted = formatOrderIdDisplay(selectedOrderForDetails.orderNumber || selectedOrderForDetails.id.toString());
                        return formatted.prefix + formatted.highlighted;
                      })()}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Status:</span>
                      <Badge className={getOrderStatusColor(selectedOrderForDetails.status)}>
                        {getOrderStatusText(selectedOrderForDetails.status)}
                      </Badge>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Order Time:</span>
                      <span>{new Date(selectedOrderForDetails.createdAt).toLocaleString()}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Est. Time:</span>
                      <span>{selectedOrderForDetails.estimatedTime || 0} minutes</span>
                    </div>
                  </div>
                </div>

                <div className="bg-gray-50 rounded-lg p-4">
                  <h4 className="font-semibold mb-2">Customer & Payment</h4>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Customer:</span>
                      <span className="font-medium">{selectedOrderForDetails.customerName}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Total Amount:</span>
                      <span className="font-bold text-xl text-green-600">â‚¹{selectedOrderForDetails.amount}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Barcode:</span>
                      <span className="font-mono text-xs">{selectedOrderForDetails.barcode}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Barcode Used:</span>
                      <span className={selectedOrderForDetails.barcodeUsed ? "text-green-600" : "text-orange-600"}>
                        {selectedOrderForDetails.barcodeUsed ? "Yes" : "No"}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-end space-x-3">
                <Button
                  variant="outline"
                  onClick={() => setShowOrderDetailPopup(false)}
                >
                  Close
                </Button>
                {selectedOrderForDetails.status === "ready" && (
                  <Button
                    onClick={(e) => {
                      e.stopPropagation();
                      setShowOrderDetailPopup(false);
                      setTimeout(() => {
                        handleScanBarcode(selectedOrderForDetails);
                      }, 100);
                    }}
                    className="bg-blue-600 hover:bg-blue-700 text-white"
                  >
                    <ScanLine className="w-4 h-4 mr-2" />
                    Scan Barcode
                  </Button>
                )}
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Cart Dialog - Alternative Modal Implementation */}
      {showCartDialog && (
        <div className="fixed inset-0 z-[100] flex items-center justify-center">
          {/* Backdrop */}
          <div 
            className="fixed inset-0 bg-black/50" 
            onClick={() => setShowCartDialog(false)}
          />

          {/* Modal Content */}
          <div className="relative bg-white rounded-lg shadow-lg max-w-lg w-[90%] max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-semibold flex items-center">
                  <Receipt className="w-5 h-5 mr-2" />
                  Order Summary - {cart.length} Dish{cart.length !== 1 ? 'es' : ''}
                </h2>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowCartDialog(false)}
                  className="h-8 w-8 p-0"
                >
                  <X className="w-4 h-4" />
                </Button>
              </div>

              <div className="space-y-4">
                {cart.length === 0 ? (
                  <div className="text-center py-8">
                    <ShoppingCart className="w-12 h-12 mx-auto mb-3 opacity-40" />
                    <h3 className="font-medium text-lg mb-1">No dishes selected</h3>
                    <p className="text-sm text-muted-foreground">Add items from the menu to start your order</p>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {/* Dish List */}
                    <div className="max-h-80 overflow-auto space-y-3">
                      <h4 className="font-semibold text-sm text-muted-foreground uppercase tracking-wide">Selected Dishes</h4>
                      {cart.map((item, index) => (
                        <div key={index} className="bg-gray-50 rounded-lg p-4 border">
                          <div className="flex items-start justify-between">
                            <div className="flex-1">
                              <h5 className="font-semibold text-base mb-1">{item.name}</h5>
                              <p className="text-sm text-muted-foreground mb-2">â‚¹{item.price} per item</p>

                              {/* Quantity Controls */}
                              <div className="flex items-center space-x-3">
                                <span className="text-sm font-medium">Quantity:</span>
                                <div className="flex items-center space-x-2">
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    className="h-7 w-7 p-0"
                                    onClick={() => {
                                      if (item.quantity > 1) {
                                        setCart(cart.map((cartItem, i) => 
                                          i === index 
                                            ? { ...cartItem, quantity: cartItem.quantity - 1 }
                                            : cartItem
                                        ));
                                      } else {
                                        setCart(cart.filter((_, i) => i !== index));
                                      }
                                    }}
                                  >
                                    <Minus className="w-3 h-3" />
                                  </Button>
                                  <span className="text-sm font-medium w-8 text-center">{item.quantity}</span>
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    className="h-7 w-7 p-0"
                                    onClick={() => {
                                      setCart(cart.map((cartItem, i) => 
                                        i === index 
                                          ? { ...cartItem, quantity: cartItem.quantity + 1 }
                                          : cartItem
                                      ));
                                    }}
                                  >
                                    <Plus className="w-3 h-3" />
                                  </Button>
                                  <Button
                                    variant="destructive"
                                    size="sm"
                                    className="h-7 w-7 p-0 ml-2"
                                    onClick={() => setCart(cart.filter((_, i) => i !== index))}
                                    title="Remove item"
                                  >
                                    <X className="w-3 h-3" />
                                  </Button>
                                </div>
                              </div>
                            </div>

                            {/* Item Total */}
                            <div className="text-right">
                              <div className="text-lg font-bold text-primary">â‚¹{item.price * item.quantity}</div>
                              <div className="text-xs text-muted-foreground">{item.quantity} Ã— â‚¹{item.price}</div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>

                    {/* Order Summary & Action */}
                    <div className="border-t pt-4 space-y-4 bg-gray-50 -mx-6 px-6 -mb-6 pb-6 rounded-b-lg">
                      <div className="space-y-2">
                        <div className="flex items-center justify-between text-lg">
                          <span className="font-semibold">Total Amount:</span>
                          <span className="font-bold text-xl text-primary">â‚¹{getTotalAmount()}</span>
                        </div>
                        <div className="flex items-center justify-between text-sm">
                          <span className="text-muted-foreground">Payment Mode:</span>
                          <Badge variant="secondary" className="font-medium">
                            {paymentMode === 'cash' ? 'Cash Payment' : 'Online Payment'}
                          </Badge>
                        </div>
                      </div>

                      {/* Action Buttons */}
                      <div className="flex space-x-3">
                        <Button 
                          variant="outline"
                          className="flex-1"
                          onClick={() => setCart([])}
                          disabled={isPlacingOrder || placeOfflineOrderMutation.isPending}
                        >
                          Clear All
                        </Button>
                        <Button 
                          className="flex-2 bg-green-600 hover:bg-green-700 text-white font-semibold" 
                          onClick={() => {
                            handlePlaceOfflineOrder();
                            setShowCartDialog(false);
                          }}
                          disabled={isPlacingOrder || placeOfflineOrderMutation.isPending}
                        >
                          {isPlacingOrder || placeOfflineOrderMutation.isPending ? (
                            <>
                              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                              Processing Order...
                            </>
                          ) : (
                            <>
                              <Receipt className="w-4 h-4 mr-2" />
                              Place Order - â‚¹{getTotalAmount()}
                            </>
                          )}
                        </Button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/CanteenOwnerMenuMa.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Switch } from "@/components/ui/switch";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogDescription } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { VegIndicator } from "@/components/ui/VegIndicator";
import type { MenuItem, Category } from "@shared/schema";
import { 
  Plus, 
  Edit, 
  Trash2, 
  Search, 
  Loader2,
  X,
  ChefHat,
  TrendingUp,
  AlertTriangle,
  Package,
  BarChart3,
  Filter,
  Minus
} from "lucide-react";

interface CanteenOwnerMenuManagementProps {
  menuItems: MenuItem[];
  categories: Category[];
  onMenuUpdate: () => void;
}

export default function CanteenOwnerMenuManagement({ 
  menuItems, 
  categories, 
  onMenuUpdate 
}: CanteenOwnerMenuManagementProps) {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [stockFilter, setStockFilter] = useState("all"); // all, low_stock, out_of_stock
  const [editingItem, setEditingItem] = useState<MenuItem | null>(null);
  const [isAddingItem, setIsAddingItem] = useState(false);
  const [isAddingCategory, setIsAddingCategory] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [editForm, setEditForm] = useState({
    name: "",
    price: "",
    categoryId: "",
    description: "",
    stock: "",
    available: true,
    isVegetarian: true,
    isMarkable: true,
    addOns: "[]"
  });
  const [addOns, setAddOns] = useState<Array<{ name: string; price: string }>>([]);
  const { toast } = useToast();

  // Enhanced mutations with comprehensive synchronization
  const updateMenuItemMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<MenuItem> }) => {
      return apiRequest(`/api/menu/${id}`, {
        method: 'PUT',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/menu'] });
      toast({
        title: "Success",
        description: "Menu item updated successfully!"
      });
      onMenuUpdate();
    },
    onError: (error) => {
      console.error('Update error:', error);
      toast({
        title: "Error",
        description: "Failed to update menu item. Please try again.",
        variant: "destructive"
      });
    }
  });

  const addMenuItemMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest('/api/menu', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/menu'] });
      toast({
        title: "Success",
        description: "Menu item added successfully!"
      });
      resetForm();
      setIsAddingItem(false);
      onMenuUpdate();
    },
    onError: (error) => {
      console.error('Add error:', error);
      toast({
        title: "Error",
        description: "Failed to add menu item. Please try again.",
        variant: "destructive"
      });
    }
  });

  const deleteMenuItemMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest(`/api/menu/${id}`, {
        method: 'DELETE'
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/menu'] });
      toast({
        title: "Success",
        description: "Menu item deleted successfully!"
      });
      onMenuUpdate();
    },
    onError: (error) => {
      console.error('Delete error:', error);
      toast({
        title: "Error",
        description: "Failed to delete menu item. Please try again.",
        variant: "destructive"
      });
    }
  });

  const addCategoryMutation = useMutation({
    mutationFn: async (data: { name: string }) => {
      return apiRequest('/api/categories', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/categories'] });
      toast({
        title: "Success",
        description: "Category added successfully!"
      });
      setNewCategoryName("");
      setIsAddingCategory(false);
      onMenuUpdate();
    },
    onError: (error) => {
      console.error('Add category error:', error);
      toast({
        title: "Error",
        description: "Failed to add category. Please try again.",
        variant: "destructive"
      });
    }
  });

  const updateStockMutation = useMutation({
    mutationFn: async ({ id, newStock }: { id: string; newStock: number }) => {
      return apiRequest(`/api/menu/${id}`, {
        method: 'PUT',
        body: JSON.stringify({ stock: newStock }),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/menu'] });
      onMenuUpdate();
    },
    onError: (error) => {
      console.error('Stock update error:', error);
      toast({
        title: "Error",
        description: "Failed to update stock. Please try again.",
        variant: "destructive"
      });
    }
  });

  const resetForm = () => {
    setEditForm({
      name: "",
      price: "",
      categoryId: "",
      description: "",
      stock: "",
      available: true,
      isVegetarian: true,
      isMarkable: true,
      addOns: "[]"
    });
    setAddOns([]);
  };

  const handleEdit = (item: MenuItem) => {
    setEditingItem(item);
    setEditForm({
      name: item.name || "",
      price: item.price?.toString() || "",
      categoryId: item.categoryId?.toString() || "",
      description: item.description || "",
      stock: item.stock?.toString() || "",
      available: item.available ?? true,
      isVegetarian: item.isVegetarian ?? true,
      isMarkable: item.isMarkable ?? true,
      addOns: item.addOns || "[]"
    });

    try {
      const parsedAddOns = JSON.parse(item.addOns || "[]");
      if (Array.isArray(parsedAddOns)) {
        setAddOns(parsedAddOns);
      }
    } catch {
      setAddOns([]);
    }
  };

  const handleSaveEdit = () => {
    if (!editingItem || !editForm.name || !editForm.price || !editForm.categoryId) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive"
      });
      return;
    }

    const updatedData = {
      name: editForm.name,
      price: parseInt(editForm.price),
      categoryId: editForm.categoryId,
      description: editForm.description,
      stock: parseInt(editForm.stock),
      available: editForm.available,
      isVegetarian: editForm.isVegetarian,
      isMarkable: editForm.isMarkable,
      addOns: JSON.stringify(addOns.filter(addon => addon.name && addon.price))
    };

    updateMenuItemMutation.mutate({ id: editingItem.id, data: updatedData });
    setEditingItem(null);
  };

  const handleAdd = () => {
    if (!editForm.name || !editForm.price || !editForm.categoryId) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive"
      });
      return;
    }

    const newItemData = {
      name: editForm.name,
      price: parseInt(editForm.price),
      categoryId: editForm.categoryId,
      description: editForm.description,
      stock: parseInt(editForm.stock) || 0,
      available: editForm.available,
      isVegetarian: editForm.isVegetarian,
      isMarkable: editForm.isMarkable,
      addOns: JSON.stringify(addOns.filter(addon => addon.name && addon.price))
    };

    addMenuItemMutation.mutate(newItemData);
  };

  const handleAddCategory = () => {
    if (!newCategoryName.trim()) {
      toast({
        title: "Error",
        description: "Please enter a category name",
        variant: "destructive"
      });
      return;
    }

    addCategoryMutation.mutate({ name: newCategoryName.trim() });
  };

  const addAddon = () => {
    setAddOns([...addOns, { name: "", price: "" }]);
  };

  const removeAddon = (index: number) => {
    setAddOns(addOns.filter((_, i) => i !== index));
  };

  const updateAddon = (index: number, field: 'name' | 'price', value: string) => {
    const updated = [...addOns];
    updated[index][field] = value;
    setAddOns(updated);
  };

  // Get real stock data for menu items
  const getStockData = (item: MenuItem) => {
    const currentStock = item.stock || 0;
    const minThreshold = 5;
    let status = "in_stock";
    if (currentStock === 0) status = "out_of_stock";
    else if (currentStock <= minThreshold) status = "low_stock";

    return {
      currentStock,
      minThreshold,
      status
    };
  };

  // Enhanced menu items with stock data
  const menuItemsWithStock = menuItems.map(item => ({
    ...item,
    stockData: getStockData(item)
  }));

  // Calculate analytics
  const totalItems = menuItemsWithStock.length;
  const lowStockItems = menuItemsWithStock.filter(item => item.stockData.status === "low_stock").length;
  const outOfStockItems = menuItemsWithStock.filter(item => item.stockData.status === "out_of_stock").length;
  const inStockItems = menuItemsWithStock.filter(item => item.stockData.status === "in_stock").length;
  const totalStockValue = menuItemsWithStock.reduce((sum, item) => sum + (item.stockData.currentStock * item.price), 0);

  // Filter menu items
  const filteredItems = menuItemsWithStock.filter(item => {
    const matchesSearch = item.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === "all" || 
      categories.find(cat => cat.id === item.categoryId)?.name === selectedCategory;
    const matchesStock = stockFilter === "all" || item.stockData.status === stockFilter;
    return matchesSearch && matchesCategory && matchesStock;
  });

  return (
    <div className="space-y-6">
      {/* Analytics Dashboard */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-2 sm:gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Package className="w-4 h-4 text-blue-500" />
              <div>
                <p className="text-xs text-muted-foreground">Total Items</p>
                <p className="text-xl font-bold">{totalItems}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <TrendingUp className="w-4 h-4 text-green-500" />
              <div>
                <p className="text-xs text-muted-foreground">In Stock</p>
                <p className="text-xl font-bold text-green-600">{inStockItems}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <AlertTriangle className="w-4 h-4 text-orange-500" />
              <div>
                <p className="text-xs text-muted-foreground">Low Stock</p>
                <p className="text-xl font-bold text-orange-600">{lowStockItems}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <BarChart3 className="w-4 h-4 text-red-500" />
              <div>
                <p className="text-xs text-muted-foreground">Out of Stock</p>
                <p className="text-xl font-bold text-red-600">{outOfStockItems}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Header and Controls */}
      <div className="flex flex-col gap-3 sm:gap-4">
        <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-3">
          <h2 className="text-lg sm:text-2xl font-bold flex items-center">
            <ChefHat className="w-5 h-5 sm:w-6 sm:h-6 mr-2" />
            Menu Management
          </h2>
          <div className="flex flex-col xs:flex-row gap-2">
          {/* Add Category Dialog */}
          <Dialog open={isAddingCategory} onOpenChange={setIsAddingCategory}>
            <DialogTrigger asChild>
              <Button variant="outline" data-testid="button-add-category">
                <Plus className="w-4 h-4 mr-2" />
                Add Category
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>Add New Category</DialogTitle>
                <DialogDescription>
                  Create a new category for your menu items.
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="category-name">Category Name *</Label>
                  <Input
                    id="category-name"
                    data-testid="input-category-name"
                    value={newCategoryName}
                    onChange={(e) => setNewCategoryName(e.target.value)}
                    placeholder="e.g., Beverages, Snacks, Main Course"
                  />
                </div>
              </div>
              <div className="flex justify-end space-x-2 pt-4 border-t">
                <Button
                  variant="outline"
                  onClick={() => {
                    setIsAddingCategory(false);
