comprehensive-analytics-test.js.content:
// Comprehensive Reports & Analytics Functionality Test
// Testing all components and data syncing

const testAnalyticsAPI = async () => {
  try {
    const response = await fetch('http://localhost:5000/api/admin/analytics');
    const data = await response.json();
    return {
      status: 'PASS',
      data: data,
      timestamp: new Date().toISOString()
    };
  } catch (error) {
    return { status: 'FAIL', error: error.message };
  }
};

const testOrdersAPI = async () => {
  try {
    const response = await fetch('http://localhost:5000/api/orders');
    const orders = await response.json();
    return {
      status: 'PASS',
      count: orders.length,
      sample: orders.slice(0, 2).map(o => ({
        orderNumber: o.orderNumber,
        amount: o.amount,
        status: o.status
      }))
    };
  } catch (error) {
    return { status: 'FAIL', error: error.message };
  }
};

const testUsersAPI = async () => {
  try {
    const response = await fetch('http://localhost:5000/api/users');
    const users = await response.json();
    return {
      status: 'PASS',
      count: users.length,
      roles: [...new Set(users.map(u => u.role))]
    };
  } catch (error) {
    return { status: 'FAIL', error: error.message };
  }
};

const testMenuAPI = async () => {
  try {
    const response = await fetch('http://localhost:5000/api/menu');
    const menu = await response.json();
    return {
      status: 'PASS',
      count: menu.length,
      available: menu.filter(item => item.available).length
    };
  } catch (error) {
    return { status: 'FAIL', error: error.message };
  }
};

// Main test execution
console.log('ðŸ§ª Starting Comprehensive Reports & Analytics Test...\n');

Promise.all([
  testAnalyticsAPI(),
  testOrdersAPI(), 
  testUsersAPI(),
  testMenuAPI()
]).then(results => {
  const [analytics, orders, users, menu] = results;

  console.log('ðŸ“Š API DATA VERIFICATION');
  console.log('========================');
