// Service Worker for Dinez Canteen PWA
const CACHE_NAME = 'dinez-canteen-v1';
const CACHE_VERSION = 'cache-v' + Date.now(); // Dynamic cache version
const APP_VERSION = '1.0.0'; // App version for tracking
const STATIC_CACHE_URLS = [
  '/',
  '/manifest.json',
  '/favicon.png',
  '/logo.png',
  '/icon-192.png',
  '/icon-512.png'
];

// Install event - cache static resources
self.addEventListener('install', event => {
  console.log('Service Worker: Installing');

  // In development, always skip waiting for immediate updates
  if (location.hostname === 'localhost' || location.hostname === '127.0.0.1' || location.hostname.includes('replit.dev')) {
    console.log('ðŸ”§ Development mode: Skipping waiting immediately');
    self.skipWaiting();
  }

  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => {
        console.log('Service Worker: Caching static files');
        return cache.addAll(STATIC_CACHE_URLS);
      })
      .catch(err => {
        console.log('Service Worker: Cache failed', err);
      })
  );
});

// Activate event - clean up old caches with version checking
self.addEventListener('activate', event => {
  console.log('Service Worker: Activating');
  event.waitUntil(
    caches.keys()
      .then(cacheNames => {
        return Promise.all(
          cacheNames.map(cache => {
            // Delete old caches or caches with different versions
            if (cache !== CACHE_NAME || !cache.includes(CACHE_VERSION)) {
              console.log('Service Worker: Deleting old cache', cache);
              return caches.delete(cache);
            }
          })
        );
      })
      .then(() => {
        console.log('Service Worker: Claiming clients');
        return self.clients.claim();
      })
  );
});

// Handle messages from main thread
self.addEventListener('message', event => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    console.log('Service Worker: Skipping waiting...');
    self.skipWaiting();
  }

  if (event.data && event.data.type === 'GET_VERSION') {
    // Send version info back to client
    event.ports[0].postMessage({
      version: APP_VERSION,
      cacheVersion: CACHE_VERSION
    });
  }
});

// Fetch event - serve cached content when offline
self.addEventListener('fetch', event => {
  // Skip non-GET requests
  if (event.request.method !== 'GET') return;

  // Skip requests to external domains
  if (!event.request.url.startsWith(self.location.origin)) return;

  event.respondWith(
    caches.match(event.request)
      .then(response => {
        // Return cached version or fetch from network
        return response || fetch(event.request)
          .then(fetchResponse => {
            // Don't cache API calls or non-successful responses
            if (!fetchResponse.ok || event.request.url.includes('/api/')) {
              return fetchResponse;
            }

            // Clone the response before caching
            const responseToCache = fetchResponse.clone();
            caches.open(CACHE_NAME)
              .then(cache => {
                cache.put(event.request, responseToCache);
              });

            return fetchResponse;
          })
          .catch(() => {
            // If both cache and network fail, show offline page for navigations
            if (event.request.destination === 'document') {
              return caches.match('/');
            }
            return new Response('Offline', { status: 503, statusText: 'Service Unavailable' });
          });
      })
  );
});

// Handle background sync (optional)
self.addEventListener('sync', event => {
  console.log('Service Worker: Background sync', event.tag);
  // Add background sync logic here if needed
});

// Handle push notifications
self.addEventListener('push', event => {
  console.log('Service Worker: Push notification received', event);

  let notificationData = {
    title: 'Canteen App',
    body: 'You have a new notification!',
    icon: '/icon-192.png',
    badge: '/icon-192.png',
    data: {},
    actions: [],
    requireInteraction: false,
    silent: false,
    tag: 'default',
    timestamp: Date.now(),
    // Android-specific settings for heads-up notifications
    priority: 'high',
    urgency: 'high',
    vibrate: [200, 100, 200],
  };

  // Parse notification data from server
  if (event.data) {
    try {
      const serverData = event.data.json();
      notificationData = {
        ...notificationData,
        ...serverData,
      };
    } catch (error) {
      console.error('Failed to parse push notification data:', error);
      notificationData.body = event.data.text() || notificationData.body;
    }
  }

  // Add default actions for order-related notifications
  if (notificationData.data?.type === 'order_update' || notificationData.data?.type === 'new_order') {
    notificationData.actions = [
      {
        action: 'view',
        title: 'View Order',
        icon: '/icon-192.png'
      },
      {
        action: 'close',
        title: 'Close',
        icon: '/icon-192.png'
      }
    ];
    notificationData.requireInteraction = true;
    // High priority for order updates to ensure heads-up display
    notificationData.priority = 'high';
    notificationData.urgency = 'high';
  }

  // Maximum Android heads-up notification settings
  const androidNotificationOptions = {
    body: notificationData.body,
    icon: notificationData.icon,
    badge: notificationData.badge,
    data: notificationData.data,
    actions: notificationData.actions,
    // Force heads-up display on Android
    requireInteraction: true,
    silent: false,
    tag: notificationData.tag,
    timestamp: notificationData.timestamp,
    vibrate: [300, 200, 300, 200, 300],
    // Maximum priority settings for Android heads-up
    renotify: true,
    sticky: true,
    // Enhanced visual settings
    image: notificationData.image,
    dir: 'ltr',
    lang: 'en',
  };

  // Show notification and send to in-app notification panel
  event.waitUntil(
    Promise.all([
      // Show browser notification with enhanced Android options
      self.registration.showNotification(notificationData.title, androidNotificationOptions),
      // Send to all clients for in-app notification panel
      self.clients.matchAll().then(clients => {
        clients.forEach(client => {
          client.postMessage({
            type: 'push_notification',
            notification: {
              title: notificationData.title,
              message: notificationData.body,
              type: notificationData.data?.notificationType || 'confirmed',
              orderNumber: notificationData.data?.orderNumber,
              timestamp: new Date(notificationData.timestamp)
            }
          });
        });
      })
    ])
  );
});

// Handle notification click events
self.addEventListener('notificationclick', event => {
  console.log('Service Worker: Notification clicked', event);

  const notification = event.notification;
  const action = event.action;
  const data = notification.data || {};

  notification.close();

  // Handle different actions
  if (action === 'close') {
    return;
  }

  // Determine URL to open
  let urlToOpen = '/';

  if (data.url) {
    urlToOpen = data.url;
  } else if (data.type === 'order_update' && data.orderNumber) {
    urlToOpen = `/orders/${data.orderNumber}`;
  } else if (data.type === 'new_order' && data.orderNumber) {
    urlToOpen = `/admin/orders/${data.orderNumber}`;
  } else if (data.type === 'payment_confirmation' && data.orderNumber) {
    urlToOpen = `/orders/${data.orderNumber}`;
  }

  // Open or focus app window
  event.waitUntil(
    clients.matchAll({ type: 'window', includeUncontrolled: true })
      .then(clientList => {
        // Try to find existing window and focus it
        for (const client of clientList) {
          if (client.url === self.location.origin + urlToOpen && 'focus' in client) {
            return client.focus();
          }
        }

        // If no existing window found, open new one
        if (clients.openWindow) {
          return clients.openWindow(urlToOpen);
        }
      })
  );
});

server/migrations/simple-schema-check.ts.content:
import mongoose from 'mongoose';
import { PrismaClient } from '@prisma/client';
import { connectToMongoDB } from '../mongodb';

/**
 * Simple Schema Validation and Auto-Migration System
 * 
 * This system ensures database compatibility across deployments by:
 * 1. Validating existing schemas
 * 2. Adding missing fields with default values
 * 3. Preserving existing data
 * 4. Running safely on every startup
 */

interface MigrationResult {
  success: boolean;
  message: string;
  details?: any;
}

export class SimpleSchemaValidator {
  private prisma: PrismaClient;
  private mongoConnected = false;

  constructor() {
    this.prisma = new PrismaClient();
  }

  async validateAndMigrateAll(): Promise<MigrationResult> {
    console.log('ðŸ”„ Starting database schema validation...');

    try {
      // Connect to MongoDB
      await connectToMongoDB();
      this.mongoConnected = true;

      // 1. Validate PostgreSQL User table
      const pgResult = await this.validatePostgreSQLSchema();
      if (!pgResult.success) {
        console.error('âŒ PostgreSQL validation failed:', pgResult.message);
      }

      // 2. Ensure MongoDB collections have proper indexes
      const mongoResult = await this.validateMongoDBSchema();
      if (!mongoResult.success) {
        console.error('âŒ MongoDB validation failed:', mongoResult.message);
      }

      // 3. Add missing fields to existing documents
      await this.addMissingMongoFields();

      console.log('âœ… Schema validation completed');

      return {
        success: true,
        message: 'Schema validation completed successfully',
        details: { postgresql: pgResult, mongodb: mongoResult }
      };

    } catch (error) {
      console.error('âŒ Schema validation error:', error);
      return {
        success: false,
        message: error instanceof Error ? error.message : 'Unknown error',
        details: { error }
      };
    } finally {
      await this.cleanup();
    }
  }

  private async validatePostgreSQLSchema(): Promise<MigrationResult> {
    try {
      console.log('ðŸ” Validating PostgreSQL schema...');

      // Check if users table exists
      const result = await this.prisma.$queryRaw`
        SELECT EXISTS (
          SELECT FROM information_schema.tables 
          WHERE table_schema = 'public' AND table_name = 'users'
        );
      ` as Array<{ exists: boolean }>;

      if (!result[0]?.exists) {
        console.log('âš ï¸ Users table missing - will be created by Prisma on first use');
      } else {
        console.log('âœ… PostgreSQL users table exists');
      }

      // Try to query the users table if it exists
      if (result[0]?.exists) {
        await this.prisma.user.findMany({ take: 1 });
        return { success: true, message: 'PostgreSQL schema validated' };
      } else {
        console.log('ðŸ“‹ To create the users table, run: npx prisma migrate dev --schema=prisma/postgres/schema.prisma');
        return { success: true, message: 'PostgreSQL schema validation skipped - users table will be created on first migration' };
      }

    } catch (error) {
      console.error('âš ï¸ PostgreSQL validation issue:', error);
      return { 
        success: false, 
        message: error instanceof Error ? error.message : 'PostgreSQL validation failed'
      };
    }
  }

  private async validateMongoDBSchema(): Promise<MigrationResult> {
    try {
      console.log('ðŸ” Validating MongoDB schema...');

      const db = mongoose.connection.db;
      if (!db) {
        throw new Error('MongoDB connection not available');
      }

      // Get all collections
      const collections = await db.listCollections().toArray();
      const collectionNames = collections.map(c => c.name);

      const expectedCollections = [
        'categories', 'menuitems', 'orders', 'orderitems', 
        'notifications', 'loginissues', 'quickorders', 'payments', 'complaints'
      ];

      for (const expected of expectedCollections) {
        if (!collectionNames.includes(expected)) {
          console.log(`âš ï¸ Collection '${expected}' will be auto-created by Mongoose`);
        } else {
          console.log(`âœ… Collection '${expected}' exists`);
        }
      }

      return { success: true, message: 'MongoDB schema validated' };

    } catch (error) {
      console.error('âš ï¸ MongoDB validation issue:', error);
      return { 
        success: false, 
        message: error instanceof Error ? error.message : 'MongoDB validation failed'
      };
    }
  }

  private async addMissingMongoFields(): Promise<void> {
    try {
      console.log('ðŸ”„ Adding missing fields to existing documents...');

      // Add isTrending field to MenuItem documents that don't have it
      if (mongoose.models.MenuItem) {
        const menuUpdates = await mongoose.models.MenuItem.updateMany(
          { isTrending: { $exists: false } },
          { $set: { isTrending: false } }
        );
        if (menuUpdates.modifiedCount > 0) {
          console.log(`âœ… Added isTrending field to ${menuUpdates.modifiedCount} menu items`);
        }
      }

      // Add updatedAt field to Payment documents that don't have it
      if (mongoose.models.Payment) {
        const paymentUpdates = await mongoose.models.Payment.updateMany(
          { updatedAt: { $exists: false } },
          { $set: { updatedAt: new Date() } }
        );
        if (paymentUpdates.modifiedCount > 0) {
          console.log(`âœ… Added updatedAt field to ${paymentUpdates.modifiedCount} payments`);
        }
      }

      // Add barcodeUsed field to Order documents that don't have it
      if (mongoose.models.Order) {
        const orderUpdates = await mongoose.models.Order.updateMany(
          { barcodeUsed: { $exists: false } },
          { $set: { barcodeUsed: false } }
        );
        if (orderUpdates.modifiedCount > 0) {
          console.log(`âœ… Added barcodeUsed field to ${orderUpdates.modifiedCount} orders`);
        }
      }

      console.log('âœ… Missing field addition completed');

    } catch (error) {
      console.error('âš ï¸ Error adding missing fields:', error);
      // Don't throw - this is non-critical
    }
  }

  private async cleanup(): Promise<void> {
    try {
      await this.prisma.$disconnect();
    } catch (error) {
      console.error('Error disconnecting from PostgreSQL:', error);
    }
  }

  async getSchemaStatus(): Promise<{
    postgresql: { connected: boolean; tablesExist: boolean };
    mongodb: { connected: boolean; collectionsCount: number };
    lastCheck: Date;
  }> {
    try {
      const postgresqlStatus = {
        connected: false,
        tablesExist: false
      };

      const mongodbStatus = {
        connected: false,
        collectionsCount: 0
      };

      // Check PostgreSQL
      try {
        await this.prisma.user.findMany({ take: 1 });
        postgresqlStatus.connected = true;
        postgresqlStatus.tablesExist = true;
      } catch (error) {
        console.error('PostgreSQL check failed:', error);
      }

      // Check MongoDB
      try {
        if (this.mongoConnected || mongoose.connection.readyState === 1) {
          mongodbStatus.connected = true;
          const db = mongoose.connection.db;
          if (db) {
            const collections = await db.listCollections().toArray();
            mongodbStatus.collectionsCount = collections.length;
          }
        }
      } catch (error) {
        console.error('MongoDB check failed:', error);
      }

      return {
        postgresql: postgresqlStatus,
        mongodb: mongodbStatus,
        lastCheck: new Date()
      };

    } catch (error) {
      return {
        postgresql: { connected: false, tablesExist: false },
        mongodb: { connected: false, collectionsCount: 0 },
        lastCheck: new Date()
      };
    }
  }
}

/**
 * Main function to run schema validation on startup
 */
export async function runStartupSchemaCheck(): Promise<boolean> {
  const validator = new SimpleSchemaValidator();

  try {
    const result = await validator.validateAndMigrateAll();

    if (result.success) {
      console.log('âœ… Database schema validation completed successfully');
      return true;
    } else {
      console.error('âš ï¸ Schema validation had issues but application will continue');
      console.error('Details:', result.message);
      return true; // Don't crash the app, just log warnings
    }

  } catch (error) {
    console.error('âŒ Schema validation failed:', error);
    console.error('ðŸ”§ Application will continue with existing schema');
    return true; // Don't crash the app
  }
}

