#!/usr/bin/env node

/**
 * Build script that ensures Firebase environment variables are available during Vite build
 * Loads environment variables from .env file for production builds
 */

import { execSync } from 'child_process';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';
import { config } from 'dotenv';
import { readFileSync, existsSync, writeFileSync } from 'fs';
import { createHash, randomBytes } from 'crypto';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const rootDir = join(__dirname, '..');

// Generate version hash for cache invalidation
const generateVersion = () => {
  const timestamp = Date.now().toString();
  const random = randomBytes(8).toString('hex');
  return `v${timestamp}-${random}`;
};

const version = generateVersion();
const buildTimestamp = Date.now().toString();
const cacheVersion = `cache-${version}`;

console.log(`ðŸ“¦ Version: ${version}`);
console.log(`ðŸ• Build timestamp: ${buildTimestamp}`);

// Load environment variables from .env file
const envPath = join(rootDir, '.env');
if (existsSync(envPath)) {
  console.log('ðŸ“‹ Loading environment variables from .env file...');
  config({ path: envPath });
} else {
  console.log('âš ï¸  No .env file found, using system environment variables');
}

// Inject version information into files
console.log('ðŸ”§ Injecting version information...');

const filesToProcess = [
  {
    path: join(rootDir, 'client/src/utils/cacheManager.ts'),
    replacements: [
      { placeholder: '__APP_VERSION__', value: version },
      { placeholder: '__BUILD_TIMESTAMP__', value: buildTimestamp }
    ]
  },
  {
    path: join(rootDir, 'client/public/sw.js'),
    replacements: [
      { placeholder: '__CACHE_VERSION__', value: cacheVersion }
    ]
  }
];

// Process each file
filesToProcess.forEach(({ path: filePath, replacements }) => {
  try {
    if (!existsSync(filePath)) {
      console.warn(`âš ï¸  File not found: ${filePath}`);
      return;
    }

    let content = readFileSync(filePath, 'utf8');

    // Apply all replacements
    replacements.forEach(({ placeholder, value }) => {
      const regex = new RegExp(placeholder, 'g');
      const matches = content.match(regex);

      if (matches) {
        content = content.replace(regex, value);
        console.log(`âœ… Replaced ${matches.length} instances of ${placeholder} in ${filePath.split('/').pop()}`);
      }
    });

    // Write the updated content back
    writeFileSync(filePath, content);

  } catch (error) {
    console.error(`âŒ Error processing ${filePath}:`, error.message);
  }
});

// Create version info file for reference
const versionInfo = {
  version,
  buildTimestamp,
  cacheVersion,
  buildDate: new Date().toISOString(),
  environment: process.env.NODE_ENV || 'production'
};

try {
  writeFileSync(join(rootDir, 'version.json'), JSON.stringify(versionInfo, null, 2));
  console.log('ðŸ“‹ Version info saved to version.json');
} catch (error) {
  console.error('âŒ Error creating version.json:', error.message);
}

// Required Firebase environment variables
const requiredFirebaseVars = [
  'VITE_FIREBASE_API_KEY',
  'VITE_FIREBASE_PROJECT_ID', 
  'VITE_FIREBASE_APP_ID'
];

console.log('ðŸ”¥ Starting production build process...');

// Check if Firebase environment variables are available
console.log('ðŸ” Checking Firebase environment variables...');
const missingVars = [];

for (const varName of requiredFirebaseVars) {
  if (!process.env[varName]) {
    missingVars.push(varName);
  } else {
    console.log(`âœ… ${varName} is available`);
  }
}

if (missingVars.length > 0) {
  console.error('âŒ Missing Firebase environment variables:');
  missingVars.forEach(varName => {
    console.error(`   - ${varName}`);
  });
  console.error('\nðŸ’¡ Add these variables to your .env file:');
  missingVars.forEach(varName => {
    console.error(`   ${varName}=your_${varName.toLowerCase().replace('vite_firebase_', '')}_here`);
  });
  console.error('\nðŸ“‹ Example .env file content:');
  console.error('   VITE_FIREBASE_API_KEY=AIza...');
  console.error('   VITE_FIREBASE_PROJECT_ID=your-project-id');
  console.error('   VITE_FIREBASE_APP_ID=1:123456...');
  process.exit(1);
}

try {
  console.log('ðŸ“¦ Building frontend with Vite...');

  // Build the frontend
  execSync('npx vite build', {
    cwd: rootDir,
    stdio: 'inherit',
    env: {
      ...process.env,
      NODE_ENV: 'production'
    }
  });

  console.log('ðŸ–¥ï¸  Building backend with esbuild...');

  // Build the backend
  execSync('npx esbuild server/index.ts --platform=node --packages=external --bundle --format=esm --outdir=dist', {
    cwd: rootDir,
    stdio: 'inherit'
  });

  console.log('âœ… Build completed successfully!');

} catch (error) {
  console.error('âŒ Build failed:', error.message);
  process.exit(1);
}

