import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  Search, 
  Package, 
  Clock, 
  User, 
  CreditCard, 
  Receipt, 
  CheckCircle, 
  AlertTriangle, 
  Loader2,
  MapPin,
  Phone
} from "lucide-react";
import { toast } from "sonner";
import { formatOrderIdDisplay } from "../../../shared/utils";

interface OrderItem {
  id: string;
  name: string;
  price: number;
  quantity: number;
  isVegetarian?: boolean;
}

interface FullOrderDetails {
  orderNumber: string;
  customerId: number;
  customerName: string;
  items: OrderItem[];
  amount: number;
  status: string;
  estimatedTime: number;
  barcode: string;
  createdAt: string;
  customerPhone?: string;
  deliveryAddress?: string;
}

interface OrderFinderModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function OrderFinderModal({ isOpen, onClose }: OrderFinderModalProps) {
  const [orderId, setOrderId] = useState("");
  const [orderDetails, setOrderDetails] = useState<FullOrderDetails | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  const fetchOrderDetails = async (orderIdToFetch: string): Promise<FullOrderDetails | null> => {
    try {
      const response = await fetch(`/api/orders/${orderIdToFetch}`);
      if (response.ok) {
        const order = await response.json();

        // Parse items if they're stored as JSON string
        let parsedItems = [];
        try {
          parsedItems = typeof order.items === 'string' ? JSON.parse(order.items) : order.items || [];
        } catch (e) {
          console.error("Error parsing order items:", e);
          parsedItems = [];
        }

        return {
          orderNumber: order.orderNumber,
          customerId: order.customerId,
          customerName: order.customerName || "Unknown Customer",
          items: parsedItems,
          amount: order.amount || 0,
          status: order.status || "unknown",
          estimatedTime: order.estimatedTime || 0,
          barcode: order.barcode || "",
          createdAt: order.createdAt || new Date().toISOString(),
          customerPhone: order.customerPhone,
          deliveryAddress: order.deliveryAddress
        };
      } else if (response.status === 404) {
        return null;
      } else {
        throw new Error('Failed to fetch order');
      }
    } catch (error) {
      console.error("Error fetching order details:", error);
      throw error;
    }
  };

  const handleSearch = async () => {
    if (!orderId.trim()) {
      toast.error("Please enter an Order ID");
      return;
    }

    // Validate 12-digit numeric format
    const orderIdPattern = /^[0-9]{12}$/;
    if (!orderIdPattern.test(orderId)) {
      toast.error("Order ID must be exactly 12 digits (numbers only)");
      return;
    }

    setIsLoading(true);
    setHasSearched(false);
    setOrderDetails(null);

    try {
      const details = await fetchOrderDetails(orderId);
      setOrderDetails(details);
      setHasSearched(true);

      if (details) {
        toast.success(`Order found: ${details.orderNumber}`);
      } else {
        toast.error("Order not found");
      }
    } catch (error) {
      toast.error("Failed to search for order");
      setHasSearched(true);
    }

    setIsLoading(false);
  };

  const handleClose = () => {
    setOrderId("");
    setOrderDetails(null);
    setHasSearched(false);
    onClose();
  };

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case "pending": return "bg-yellow-500 text-white";
      case "preparing": return "bg-orange-500 text-white";
      case "ready": return "bg-blue-500 text-white";
      case "completed": case "delivered": return "bg-green-500 text-white";
      case "cancelled": return "bg-red-500 text-white";
      default: return "bg-gray-500 text-white";
    }
  };

  const formatDateTime = (dateString: string) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString('en-IN', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return dateString;
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-2xl max-h-[95vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-2">
            <Search className="w-5 h-5" />
            <span>Find Order</span>
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Search Section */}
          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="findOrderId">Enter Order ID</Label>
              <div className="flex space-x-2">
                <Input
                  id="findOrderId"
                  placeholder="e.g., 123456789012"
                  value={orderId}
                  onChange={(e) => setOrderId(e.target.value.replace(/[^0-9]/g, ''))}
                  maxLength={12}
                  className="flex-1"
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' && !isLoading) {
                      handleSearch();
                    }
                  }}
                />
                <Button 
                  onClick={handleSearch}
                  disabled={!orderId.trim() || isLoading}
                  className="min-w-[100px]"
                >
                  {isLoading ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Searching...
                    </>
                  ) : (
                    <>
                      <Search className="w-4 h-4 mr-2" />
                      Search
                    </>
                  )}
                </Button>
              </div>
            </div>

            {orderId && (
              <Alert className={
                /^[0-9]{12}$/.test(orderId) 
                  ? "border-green-500/50 text-green-600 dark:border-green-500 [&>svg]:text-green-600"
                  : "border-amber-500/50 text-amber-600 dark:border-amber-500 [&>svg]:text-amber-600"
              }>
                {/^[0-9]{12}$/.test(orderId) ? (
                  <CheckCircle className="h-4 w-4" />
                ) : (
                  <AlertTriangle className="h-4 w-4" />
                )}
                <AlertDescription>
                  {/^[0-9]{12}$/.test(orderId) 
                    ? (
                        <div className="flex items-center">
                          <span>Valid Order ID: </span>
                          <span>{(() => {
                            const formatted = formatOrderIdDisplay(orderId);
                            return formatted.prefix;
                          })()}</span>
                          <span className="bg-green-500/20 text-green-700 font-bold px-1 rounded ml-0">
                            {(() => {
                              const formatted = formatOrderIdDisplay(orderId);
                              return formatted.highlighted;
                            })()}
                          </span>
                        </div>
                      )
                    : `Invalid format. Expected 12 digits (0-9): ${orderId.length}/12`
                  }
                </AlertDescription>
              </Alert>
            )}
          </div>

          {/* Order Details Section */}
          {hasSearched && (
            <>
              <Separator />
              {orderDetails ? (
                <div className="space-y-6">
                  {/* Order Header */}
                  <div className="text-center space-y-3">
                    <div className="flex justify-center">
                      <Badge className={getStatusColor(orderDetails.status)}>
                        {orderDetails.status.charAt(0).toUpperCase() + orderDetails.status.slice(1)}
                      </Badge>
                    </div>
                    <div>
                      <h3 className="text-xl font-bold">Order #{orderDetails.orderNumber}</h3>
                      <p className="text-sm text-muted-foreground flex items-center justify-center mt-1">
                        <Clock className="w-4 h-4 mr-1" />
                        Placed on {formatDateTime(orderDetails.createdAt)}
                      </p>
                    </div>
                  </div>

                  {/* Customer Information */}
                  <div className="bg-accent/30 rounded-lg p-4 space-y-3">
                    <h4 className="font-semibold flex items-center">
                      <User className="w-4 h-4 mr-2" />
                      Customer Information
                    </h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-3 text-sm">
                      <div>
                        <span className="font-medium">Name:</span>
                        <span className="ml-2 text-muted-foreground">{orderDetails.customerName}</span>
                      </div>
                      <div>
                        <span className="font-medium">Customer ID:</span>
                        <span className="ml-2 text-muted-foreground">#{orderDetails.customerId}</span>
                      </div>
                      {orderDetails.customerPhone && (
                        <div className="flex items-center">
                          <Phone className="w-3 h-3 mr-1" />
                          <span className="font-medium">Phone:</span>
                          <span className="ml-2 text-muted-foreground">{orderDetails.customerPhone}</span>
                        </div>
                      )}
                      {orderDetails.deliveryAddress && (
                        <div className="flex items-center">
                          <MapPin className="w-3 h-3 mr-1" />
                          <span className="font-medium">Address:</span>
                          <span className="ml-2 text-muted-foreground">{orderDetails.deliveryAddress}</span>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Order Items */}
                  <div className="space-y-4">
                    <h4 className="font-semibold flex items-center">
                      <Receipt className="w-4 h-4 mr-2" />
                      Order Items ({orderDetails.items.length})
                    </h4>
                    <div className="space-y-3">
                      {orderDetails.items.map((item, index) => (
                        <div key={`${item.id}-${index}`} className="flex items-center justify-between p-3 bg-background border rounded-lg">
                          <div className="flex-1">
                            <div className="flex items-center space-x-2">
                              <h5 className="font-medium">{item.name}</h5>
                              {item.isVegetarian !== undefined && (
                                <div className={`w-3 h-3 rounded border-2 ${item.isVegetarian ? 'border-green-600' : 'border-red-600'}`}>
                                  <div className={`w-1.5 h-1.5 rounded-full ${item.isVegetarian ? 'bg-green-600' : 'bg-red-600'} m-0.5`}></div>
                                </div>
                              )}
                            </div>
                            <p className="text-sm text-muted-foreground">â‚¹{item.price} Ã— {item.quantity}</p>
                          </div>
                          <div className="text-right">
                            <p className="font-semibold text-lg">â‚¹{item.price * item.quantity}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Order Summary */}
                  <div className="bg-primary/5 rounded-lg p-4">
                    <div className="flex items-center justify-between mb-3">
                      <span className="font-semibold flex items-center">
                        <CreditCard className="w-4 h-4 mr-2" />
                        Order Total
                      </span>
                      <span className="text-2xl font-bold text-primary">â‚¹{orderDetails.amount}</span>
                    </div>

                    {orderDetails.estimatedTime > 0 && (
                      <div className="flex items-center justify-between text-sm text-muted-foreground">
                        <span>Estimated Preparation Time:</span>
                        <span>{orderDetails.estimatedTime} minutes</span>
                      </div>
                    )}

                    {orderDetails.barcode && (
                      <div className="mt-3 pt-3 border-t">
                        <div className="flex items-center justify-between text-sm">
                          <span className="font-medium">Barcode:</span>
                          <span className="font-mono text-xs bg-muted px-2 py-1 rounded">{orderDetails.barcode}</span>
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Action Buttons */}
                  <div className="flex space-x-3 pt-4">
                    <Button
                      variant="outline"
                      onClick={handleClose}
                      className="flex-1"
                    >
                      Close
                    </Button>
                    {(orderDetails.status === "ready" || orderDetails.status === "preparing") && (
                      <Button
                        onClick={() => {
                          toast.success(`Order ${orderDetails.orderNumber} status noted!`);
                          handleClose();
                        }}
                        className="flex-1"
                      >
                        <Package className="w-4 h-4 mr-2" />
                        Update Status
                      </Button>
                    )}
                  </div>
                </div>
              ) : (
                <div className="text-center py-8 space-y-3">
                  <div className="w-16 h-16 mx-auto bg-muted rounded-full flex items-center justify-center">
                    <Package className="w-8 h-8 text-muted-foreground" />
                  </div>
                  <div>
                    <h3 className="font-semibold">Order Not Found</h3>
                    <p className="text-sm text-muted-foreground mt-1">
                      No order found with ID: {orderId}
                    </p>
                    <p className="text-xs text-muted-foreground mt-2">
                      Please check the Order ID and try again
                    </p>
                  </div>
                </div>
              )}
            </>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

