// PWA Authentication Utilities
// Handles authentication persistence and detection for Progressive Web App installations

interface StoredUser {
  id: string | number;
  name: string;
  email: string;
  role: string;
  phoneNumber?: string;
  registerNumber?: string;
  department?: string;
  currentStudyYear?: string;
  isPassed?: boolean;
  staffId?: string;
}

export function isPWAInstalled(): boolean {
  // Multiple ways to detect PWA installation
  return (
    window.matchMedia('(display-mode: standalone)').matches ||
    (window.navigator as any).standalone === true ||
    localStorage.getItem('pwa_installed') === 'true' ||
    new URLSearchParams(window.location.search).get('pwa') === 'true'
  );
}

export function getPWAAuthState(): { isAuthenticated: boolean; user: StoredUser | null; debug: any } {
  const storedUser = localStorage.getItem('user');
  const sessionTimestamp = localStorage.getItem('session_timestamp');
  const lastActivity = localStorage.getItem('last_activity');

  const debug = {
    storedUser: !!storedUser,
    storedUserLength: storedUser?.length || 0,
    sessionTimestamp: !!sessionTimestamp,
    sessionTimestampValue: sessionTimestamp,
    lastActivity: !!lastActivity,
    lastActivityValue: lastActivity,
    isPWA: isPWAInstalled(),
    currentTime: Date.now(),
    localStorageKeys: Object.keys(localStorage)
  };

  console.log("PWA Auth State Debug:", debug);

  if (!storedUser || !sessionTimestamp) {
    return { isAuthenticated: false, user: null, debug };
  }

  try {
    const userData = JSON.parse(storedUser);
    const loginTime = parseInt(sessionTimestamp);
    const maxSessionDuration = 90 * 24 * 60 * 60 * 1000; // 90 days
    const currentTime = Date.now();

    const sessionDebug = {
      ...debug,
      parsedUser: userData,
      loginTime,
      currentTime,
      sessionAge: currentTime - loginTime,
      maxDuration: maxSessionDuration,
      isValidSession: currentTime - loginTime < maxSessionDuration
    };

    console.log("PWA Session Validation:", sessionDebug);

    if (currentTime - loginTime < maxSessionDuration) {
      // Update session timestamp to extend session
      localStorage.setItem('session_timestamp', currentTime.toString());
      localStorage.setItem('last_activity', currentTime.toString());

      return { 
        isAuthenticated: true, 
        user: userData, 
        debug: { ...sessionDebug, action: 'session_extended' }
      };
    } else {
      // Session expired
      localStorage.removeItem('user');
      localStorage.removeItem('session_timestamp');
      localStorage.removeItem('last_activity');

      return { 
        isAuthenticated: false, 
        user: null, 
        debug: { ...sessionDebug, action: 'session_expired' }
      };
    }
  } catch (error) {
    console.error("PWA Auth Error:", error);
    // Clear corrupted data
    localStorage.removeItem('user');
    localStorage.removeItem('session_timestamp');
    localStorage.removeItem('last_activity');

    return { 
      isAuthenticated: false, 
      user: null, 
      debug: { ...debug, error: (error as Error)?.message || 'Unknown error', action: 'data_corrupted' }
    };
  }
}

export function setPWAAuth(userData: StoredUser): void {
  const currentTime = Date.now();

  console.log("Setting PWA Auth:", userData);

  localStorage.setItem('user', JSON.stringify(userData));
  localStorage.setItem('session_timestamp', currentTime.toString());
  localStorage.setItem('last_activity', currentTime.toString());
  localStorage.setItem('pwa_installed', 'true');

  console.log("PWA Auth Set - localStorage updated:", {
    user: localStorage.getItem('user'),
    sessionTimestamp: localStorage.getItem('session_timestamp'),
    lastActivity: localStorage.getItem('last_activity')
  });
}

export function clearPWAAuth(): void {
  console.log("Clearing PWA Auth");

  localStorage.removeItem('user');
  localStorage.removeItem('session_timestamp');
  localStorage.removeItem('last_activity');
  localStorage.removeItem('pwa_installed');
}

scripts/update-version.js.content:
#!/usr/bin/env node

// Simple version update script for deployments
const fs = require('fs');
const path = require('path');

// Generate new version info
const buildTime = Date.now();
const version = process.env.APP_VERSION || '1.0.0';
const environment = process.env.NODE_ENV || 'production';

// Update version.ts file
const versionFilePath = path.join(__dirname, '../client/src/utils/version.ts');
const versionContent = `// App version and build information (auto-generated)
export const APP_CONFIG = {
  version: '${version}',
  buildTime: ${buildTime},
  environment: '${environment}'
};

// Generate build hash for cache busting
export const getBuildHash = (): string => {
  return APP_CONFIG.buildTime.toString(36);
};

// Get full version string
export const getVersionString = (): string => {
  return \`v\${APP_CONFIG.version} (\${APP_CONFIG.environment})\`;
};

// Check if this is a production build
export const isProduction = (): boolean => {
  return APP_CONFIG.environment === 'production';
};

// Get cache-busting query parameter
export const getCacheBuster = (): string => {
  return \`?v=\${getBuildHash()}\`;
};`;

try {
  fs.writeFileSync(versionFilePath, versionContent);
  console.log(`âœ… Version updated: ${version} (${new Date(buildTime).toISOString()})`);

  // Update service worker cache version
  const swFilePath = path.join(__dirname, '../client/public/sw.js');
  if (fs.existsSync(swFilePath)) {
    let swContent = fs.readFileSync(swFilePath, 'utf8');

    // Update cache version in service worker
    const newCacheVersion = `cache-v${buildTime}-${Math.random().toString(36).substr(2, 9)}`;
    swContent = swContent.replace(
      /const CACHE_VERSION = '[^']+';/,
      `const CACHE_VERSION = '${newCacheVersion}';`
    );

    // Update app version
    swContent = swContent.replace(
      /const APP_VERSION = '[^']+';/,
      `const APP_VERSION = '${version}';`
    );

    fs.writeFileSync(swFilePath, swContent);
    console.log(`âœ… Service worker updated with cache version: ${newCacheVersion}`);
  }

} catch (error) {
  console.error('âŒ Error updating version:', error);
  process.exit(1);
}

client/src/components/AppUpdateButton.ts.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { RefreshCw, Download, Zap, Info } from "lucide-react";
import { UpdateManager } from "@/utils/updateManager";
import { passiveUpdateDetector } from "@/utils/passiveUpdateDetector";
import { toast } from "sonner";

export default function AppUpdateButton() {
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [updateAvailable, setUpdateAvailable] = useState(false);
  const [versionInfo, setVersionInfo] = useState<{ version: string; cacheVersion: string }>({
    version: '1.0.0',
    cacheVersion: 'unknown'
  });

  useEffect(() => {
    // Check for version info
    UpdateManager.getVersionInfo().then(setVersionInfo);

    // Check if update is available (this would be set by the update manager)
    const checkUpdateStatus = () => {
      const manager = UpdateManager.getInstance();
      setUpdateAvailable(manager.isUpdateReady());
    };

    checkUpdateStatus();

    // Check periodically
    const interval = setInterval(checkUpdateStatus, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleForceRefresh = async () => {
    setIsRefreshing(true);
    try {
      await UpdateManager.forceRefresh();
    } catch (error) {
      console.error('Force refresh failed:', error);
      toast.error('Refresh failed. Please reload manually.');
    } finally {
      setIsRefreshing(false);
    }
  };

  const handleCheckForUpdates = async () => {
    await passiveUpdateDetector.manualCheck();
  };

  const handleUpdateApp = () => {
    const manager = UpdateManager.getInstance();
    manager.applyUpdate();
  };

  return (
    <Card className="w-full max-w-md">
      <CardContent className="p-4">
        <div className="space-y-4">
          {/* Header */}
          <div className="flex items-center justify-between">
            <h3 className="font-semibold flex items-center">
              <Zap className="w-4 h-4 mr-2 text-blue-600" />
              App Updates
            </h3>
            {updateAvailable && (
              <Badge variant="secondary" className="bg-green-100 text-green-800">
                <Download className="w-3 h-3 mr-1" />
                Available
              </Badge>
            )}
          </div>

          {/* Version Info */}
          <div className="text-sm text-gray-600">
            <div className="flex items-center justify-between">
              <span>Version:</span>
              <span className="font-mono">{versionInfo.version}</span>
            </div>
          </div>

          {/* Update Available Section */}
          {updateAvailable && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-3">
              <div className="flex items-start">
                <Info className="w-4 h-4 text-green-600 mt-0.5 mr-2 flex-shrink-0" />
                <div className="text-sm">
                  <p className="text-green-800 font-medium">New version ready!</p>
                  <p className="text-green-700 mt-1">
                    An updated version with improvements is available.
                  </p>
                </div>
              </div>
              <Button 
                onClick={handleUpdateApp}
                className="w-full mt-3 bg-green-600 hover:bg-green-700"
                size="sm"
                data-testid="button-update-app"
              >
                <Download className="w-4 h-4 mr-2" />
                Update Now
              </Button>
            </div>
          )}

          {/* Manual Check Section */}
          <div className="border-t pt-3">
            <p className="text-sm text-gray-600 mb-3">
              Check manually for server updates when you need them.
            </p>
            <Button 
              onClick={handleCheckForUpdates}
              variant="outline"
              size="sm"
              className="w-full mb-2"
              data-testid="button-check-updates"
            >
              <Download className="w-4 h-4 mr-2" />
              Check for Updates
            </Button>
            <Button 
              onClick={handleForceRefresh}
              disabled={isRefreshing}
              variant="outline"
              size="sm"
              className="w-full"
              data-testid="button-force-refresh"
            >
              {isRefreshing ? (
                <>
                  <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                  Refreshing...
                </>
              ) : (
                <>
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Force Refresh
                </>
              )}
            </Button>
          </div>

          {/* Instructions */}
          <div className="text-xs text-gray-500 bg-gray-50 rounded p-2">
            <p><strong>Note:</strong> Updates are automatically detected. You don't need to reinstall the app - just click "Update Now" when available.</p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/utils/devUpdateDetector.ts.content:
// Smart Update Detector - efficient server restart detection
import { toast } from 'sonner';

export class DevUpdateDetector {
  private static instance: DevUpdateDetector;
  private serverStartTime: number | null = null;
  private lastKnownStartTime: number | null = null;
  private isChecking = false;

  static getInstance(): DevUpdateDetector {
    if (!DevUpdateDetector.instance) {
      DevUpdateDetector.instance = new DevUpdateDetector();
    }
    return DevUpdateDetector.instance;
  }

  /**
   * Start monitoring - check once after splash screen
   */
  startMonitoring(): void {
    console.log('ðŸ§  Starting server restart detection (one-time check)...');

    // Get stored server start time from localStorage
    this.lastKnownStartTime = this.getStoredStartTime();
    console.log('ðŸ“ Last known server start time:', this.lastKnownStartTime ? new Date(this.lastKnownStartTime) : 'None stored');

    // Check once on app load
    this.checkForServerRestart();

    // Listen for visibility changes (when user comes back to app)
    this.setupVisibilityListener();
  }



  /**
   * Get stored server start time from localStorage
   */
  private getStoredStartTime(): number | null {
    const stored = localStorage.getItem('last_server_start_time');
    return stored ? parseInt(stored) : null;
  }

  /**
   * Store server start time in localStorage
   */
  private storeStartTime(startTime: number): void {
    localStorage.setItem('last_server_start_time', startTime.toString());
  }


  /**
   * Setup visibility listener - check when user returns to app
   */
  private setupVisibilityListener(): void {
    document.addEventListener('visibilitychange', () => {
      if (!document.hidden) {
        console.log('ðŸ‘€ User returned to app, checking for server restart...');
        this.checkForServerRestart();
      }
    });

    // Also check when page gains focus
    window.addEventListener('focus', () => {
      console.log('ðŸŽ¯ Window gained focus, checking for server restart...');
      this.checkForServerRestart();
    });
  }

  /**
   * Get server start time from a simple endpoint
   */
  private async getServerStartTime(): Promise<number> {
    try {
      const response = await fetch('/api/server-info');
      if (response.ok) {
        const data = await response.json();
        return data.startTime || Date.now();
      }
    } catch (error) {
      console.log('Could not get server start time, using current time');
    }
    return Date.now();
  }

  /**
   * Check for server restart - compare timestamps
   */
  private async checkForServerRestart(): Promise<void> {
    if (this.isChecking) return;

    this.isChecking = true;

    try {
      const currentStartTime = await this.getServerStartTime();
      console.log('ðŸ” Checking server start time:', new Date(currentStartTime));

      // If we have a stored time and current time is newer, server restarted
      if (this.lastKnownStartTime && currentStartTime > this.lastKnownStartTime) {
        console.log('ðŸš¨ SERVER RESTART DETECTED!');
        console.log('ðŸ“… Previous start time:', new Date(this.lastKnownStartTime));
        console.log('ðŸ“… Current start time:', new Date(currentStartTime));
        console.log('â° Time difference:', Math.round((currentStartTime - this.lastKnownStartTime) / 1000), 'seconds');

        this.handleServerRestart();
      } else if (!this.lastKnownStartTime) {
        console.log('ðŸ“ First time check - storing server start time');
      } else {
        console.log('âœ… No server restart detected (same start time)');
      }

      // Always update the known start time
      this.lastKnownStartTime = currentStartTime;
      this.storeStartTime(currentStartTime);

    } catch (error) {
      console.error('âŒ Error checking server status:', error);
      console.log('Server might be restarting or unreachable...');
    } finally {
      this.isChecking = false;
    }
  }

  /**
   * Handle server restart - show full-screen mandatory update overlay
   */
  private handleServerRestart(): void {
    // Create full-screen mandatory update overlay
    this.showMandatoryUpdateModal();

    // Also log it prominently for developers
    console.log('ðŸš€ SERVER RESTART DETECTED - Showing mandatory update overlay');
  }

  /**
   * Show mandatory full-screen update modal
   */
  private showMandatoryUpdateModal(): void {
    // Remove any existing modal
    this.removeMandatoryUpdateModal();

    // Create full-screen overlay
    const overlay = document.createElement('div');
    overlay.id = 'mandatory-update-overlay';
    overlay.innerHTML = `
      <div class="mandatory-update-overlay">
