// Push Notification Service for Web Push API
import webpush from 'web-push';
import { storage } from './storage-hybrid';

// VAPID keys for push notifications (generate new ones for production)
const VAPID_KEYS = {
  publicKey: 'BMxJkEBb0v0EDPLfgHKlNmU8PL-oJ6uR-DaV-ZtI9M9z3OWlf5L-yAxsWTMRUhB6I2-t2I-CWBHGQCGdW_7kRxE',
  privateKey: 'QcXHhVjINogxrBhHq7TaV0Bw7BHVkj8BUz1dVCBdoqs'
};

// Configure web-push
webpush.setVapidDetails(
  'mailto:support@dinez.com',
  VAPID_KEYS.publicKey,
  VAPID_KEYS.privateKey
);

interface PushSubscription {
  id?: string;
  endpoint: string;
  keys: {
    p256dh: string;
    auth: string;
  };
  userId?: string;
  userRole?: string;
  createdAt?: Date;
}

interface NotificationPayload {
  title: string;
  body: string;
  icon?: string;
  badge?: string;
  image?: string;
  data?: any;
  tag?: string;
  url?: string;
}

class PushNotificationService {
  private subscriptions = new Map<string, PushSubscription>();

  constructor() {
    this.loadSubscriptionsFromStorage();
  }

  // Load existing subscriptions from storage (implement as needed)
  private async loadSubscriptionsFromStorage(): Promise<void> {
    try {
      // In a real implementation, load from database
      console.log('ðŸ“± Push notification service initialized');
    } catch (error) {
      console.error('Error loading push subscriptions:', error);
    }
  }

  // Get VAPID public key
  getVapidPublicKey(): string {
    return VAPID_KEYS.publicKey;
  }

  // Subscribe user to push notifications
  async subscribe(subscriptionData: PushSubscription): Promise<boolean> {
    try {
      const subscriptionKey = subscriptionData.endpoint;

      // Store subscription
      this.subscriptions.set(subscriptionKey, {
        ...subscriptionData,
        createdAt: new Date()
      });

      console.log(`âœ… User subscribed to push notifications:`, {
        userId: subscriptionData.userId,
        role: subscriptionData.userRole,
        endpoint: subscriptionData.endpoint.substring(0, 50) + '...'
      });

      return true;
    } catch (error) {
      console.error('âŒ Error subscribing to push notifications:', error);
      return false;
    }
  }

  // Unsubscribe user from push notifications
  async unsubscribe(endpoint: string): Promise<boolean> {
    try {
      if (this.subscriptions.has(endpoint)) {
        this.subscriptions.delete(endpoint);
        console.log('âœ… User unsubscribed from push notifications');
        return true;
      }
      return false;
    } catch (error) {
      console.error('âŒ Error unsubscribing from push notifications:', error);
      return false;
    }
  }

  // Send push notification to specific user
  async sendNotificationToUser(userId: string, payload: NotificationPayload): Promise<boolean> {
    try {
      const userSubscriptions = Array.from(this.subscriptions.values())
        .filter(sub => sub.userId === userId);

      if (userSubscriptions.length === 0) {
        console.log(`ðŸ“± No push subscriptions found for user ${userId}`);
        return false;
      }

      const pushPromises = userSubscriptions.map(subscription => 
        this.sendPushNotification(subscription, payload)
      );

      const results = await Promise.allSettled(pushPromises);
      const successCount = results.filter(r => r.status === 'fulfilled' && r.value).length;

      console.log(`ðŸ“¤ Sent ${successCount}/${userSubscriptions.length} notifications to user ${userId}`);
      return successCount > 0;
    } catch (error) {
      console.error('âŒ Error sending notification to user:', error);
      return false;
    }
  }

  // Send push notification to users by role
  async sendNotificationToRole(role: string, payload: NotificationPayload): Promise<number> {
    try {
      const roleSubscriptions = Array.from(this.subscriptions.values())
        .filter(sub => sub.userRole === role);

      if (roleSubscriptions.length === 0) {
        console.log(`ðŸ“± No push subscriptions found for role ${role}`);
        return 0;
      }

      const pushPromises = roleSubscriptions.map(subscription => 
        this.sendPushNotification(subscription, payload)
      );

      const results = await Promise.allSettled(pushPromises);
      const successCount = results.filter(r => r.status === 'fulfilled' && r.value).length;

      console.log(`ðŸ“¤ Sent ${successCount}/${roleSubscriptions.length} notifications to role ${role}`);
      return successCount;
    } catch (error) {
      console.error('âŒ Error sending notifications to role:', error);
      return 0;
    }
  }

  // Send push notification to all subscribers
  async sendNotificationToAll(payload: NotificationPayload): Promise<number> {
    try {
      const allSubscriptions = Array.from(this.subscriptions.values());

      if (allSubscriptions.length === 0) {
        console.log('ðŸ“± No push subscriptions found');
        return 0;
      }

      const pushPromises = allSubscriptions.map(subscription => 
        this.sendPushNotification(subscription, payload)
      );

      const results = await Promise.allSettled(pushPromises);
      const successCount = results.filter(r => r.status === 'fulfilled' && r.value).length;

      console.log(`ðŸ“¤ Sent ${successCount}/${allSubscriptions.length} notifications to all users`);
      return successCount;
    } catch (error) {
      console.error('âŒ Error sending notifications to all:', error);
      return 0;
    }
  }

  // Send order ready notification
  async sendOrderReadyNotification(orderId: string, customerName: string, orderNumber: string): Promise<boolean> {
    try {
      // Get order details to find customer
      const order = await storage.getOrder(orderId);
      if (!order) {
        console.log(`âŒ Order ${orderId} not found`);
        return false;
      }

      const payload: NotificationPayload = {
        title: 'ðŸ½ï¸ Order Ready for Pickup!',
        body: `Hi ${customerName}! Your order #${orderNumber} is ready for pickup at the canteen.`,
        icon: '/icon-192.png',
        badge: '/icon-192.png',
        data: {
          orderId,
          orderNumber,
          type: 'order-ready'
        },
        tag: `order-ready-${orderId}`,
        url: `/order-status/${orderId}`
      };

      // Send to specific customer (if we have their user ID)
      if (order.customerId) {
        return await this.sendNotificationToUser(order.customerId.toString(), payload);
      }

      return false;
    } catch (error) {
      console.error('âŒ Error sending order ready notification:', error);
      return false;
    }
  }

  // Send single push notification
  private async sendPushNotification(subscription: PushSubscription, payload: NotificationPayload): Promise<boolean> {
    try {
      const pushSubscription = {
        endpoint: subscription.endpoint,
        keys: {
          p256dh: subscription.keys.p256dh,
          auth: subscription.keys.auth
        }
      };

      await webpush.sendNotification(
        pushSubscription,
        JSON.stringify(payload),
        {
          vapidDetails: {
            subject: 'mailto:support@dinez.com',
            publicKey: VAPID_KEYS.publicKey,
            privateKey: VAPID_KEYS.privateKey
          },
          TTL: 60 * 60 * 24, // 24 hours
          urgency: 'normal'
        }
      );

      return true;
    } catch (error) {
      console.error('âŒ Error sending push notification:', error);

      // Remove invalid subscriptions
      if (error && typeof error === 'object' && 'statusCode' in error) {
        const statusCode = (error as any).statusCode;
        if (statusCode === 410 || statusCode === 413) {
          console.log('ðŸ§¹ Removing invalid subscription');
          this.subscriptions.delete(subscription.endpoint);
        }
      }

      return false;
    }
  }

  // Get subscription count
  getSubscriptionCount(): number {
    return this.subscriptions.size;
  }

  // Get subscriptions by role
  getSubscriptionsByRole(role: string): PushSubscription[] {
    return Array.from(this.subscriptions.values())
      .filter(sub => sub.userRole === role);
  }

  // Clean up expired subscriptions
  cleanupExpiredSubscriptions(): void {
    const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);

    for (const [key, subscription] of this.subscriptions.entries()) {
      if (subscription.createdAt && subscription.createdAt < thirtyDaysAgo) {
        this.subscriptions.delete(key);
      }
    }

    console.log(`ðŸ§¹ Cleaned up expired subscriptions. Current count: ${this.subscriptions.size}`);
  }
}

// Export singleton instance
export const pushNotificationService = new PushNotificationService();

// Export types
export type { PushSubscription, NotificationPayload };

