// Hook for managing push notification setup
import { useState, useEffect } from 'react';
import { pushNotificationManager } from '@/utils/pushNotifications';

interface UseNotificationSetupReturn {
  showPermissionDialog: boolean;
  setShowPermissionDialog: (show: boolean) => void;
  permissionStatus: NotificationPermission;
  isSupported: boolean;
  initializeNotifications: () => Promise<void>;
}

export function useNotificationSetup(): UseNotificationSetupReturn {
  const [showPermissionDialog, setShowPermissionDialog] = useState(false);
  const [permissionStatus, setPermissionStatus] = useState<NotificationPermission>('default');
  const [isSupported, setIsSupported] = useState(false);

  useEffect(() => {
    const initializeOnLoad = async () => {
      // Check if notifications are supported
      const supported = pushNotificationManager.isNotificationSupported();
      setIsSupported(supported);

      if (supported) {
        const status = pushNotificationManager.getPermissionStatus();
        setPermissionStatus(status);

        // Check if we should show permission dialog
        const hasShownDialog = localStorage.getItem('notification-dialog-shown');
        const userData = localStorage.getItem('userData');

        // Only show dialog if:
        // 1. Notifications are supported
        // 2. User is logged in
        // 3. Permission is still default (not granted or denied)
        // 4. We haven't shown the dialog before (or it's been more than 7 days)
        if (userData && status === 'default') {
          const lastShown = hasShownDialog ? parseInt(hasShownDialog) : 0;
          const sevenDaysAgo = Date.now() - (7 * 24 * 60 * 60 * 1000);

          if (lastShown < sevenDaysAgo) {
            // Small delay to let the app load first
            setTimeout(() => {
              setShowPermissionDialog(true);
            }, 2000);
          }
        }
      }
    };

    initializeOnLoad();
  }, []);

  const handleDialogClose = (open: boolean) => {
    setShowPermissionDialog(open);
    if (!open) {
      // Mark that we've shown the dialog
      localStorage.setItem('notification-dialog-shown', Date.now().toString());
    }
  };

  const initializeNotifications = async () => {
    if (isSupported) {
      const success = await pushNotificationManager.initializeForUser();
      if (success) {
        setPermissionStatus('granted');
        // Set up notification click handler
        pushNotificationManager.setupNotificationClickHandler();
      }
    }
  };

  return {
    showPermissionDialog,
    setShowPermissionDialog: handleDialogClose,
    permissionStatus,
    isSupported,
    initializeNotifications
  };
}

