// Push Notification Utilities for PWA
// Works on both iOS Safari and Android Chrome

interface PushSubscriptionData {
  endpoint: string;
  keys: {
    p256dh: string;
    auth: string;
  };
  userId?: string;
  userRole?: string;
}

interface NotificationData {
  title: string;
  body: string;
  icon?: string;
  badge?: string;
  image?: string;
  data?: any;
  tag?: string;
  url?: string;
}

class PushNotificationManager {
  private vapidPublicKey = 'BMxJkEBb0v0EDPLfgHKlNmU8PL-oJ6uR-DaV-ZtI9M9z3OWlf5L-yAxsWTMRUhB6I2-t2I-CWBHGQCGdW_7kRxE'; // Will be set from server
  private isSupported = false;
  private isPermissionGranted = false;
  private subscription: PushSubscription | null = null;

  constructor() {
    this.checkSupport();
  }

  // Check if push notifications are supported
  private checkSupport(): boolean {
    if (!('serviceWorker' in navigator)) {
      console.log('âŒ Service workers not supported');
      return false;
    }

    if (!('PushManager' in window)) {
      console.log('âŒ Push notifications not supported');
      return false;
    }

    if (!('Notification' in window)) {
      console.log('âŒ Notifications API not supported');
      return false;
    }

    this.isSupported = true;
    console.log('âœ… Push notifications supported');
    return true;
  }

  // Request notification permission
  async requestPermission(): Promise<boolean> {
    if (!this.isSupported) {
      return false;
    }

    try {
      const permission = await Notification.requestPermission();
      this.isPermissionGranted = permission === 'granted';

      console.log('ðŸ”” Notification permission:', permission);

      if (this.isPermissionGranted) {
        await this.subscribeToPushNotifications();
      }

      return this.isPermissionGranted;
    } catch (error) {
      console.error('âŒ Error requesting notification permission:', error);
      return false;
    }
  }

  // Check current permission status
  getPermissionStatus(): NotificationPermission {
    return Notification.permission;
  }

  // Subscribe to push notifications
  async subscribeToPushNotifications(): Promise<PushSubscription | null> {
    if (!this.isSupported || !this.isPermissionGranted) {
      return null;
    }

    try {
      const registration = await navigator.serviceWorker.ready;

      // Get VAPID public key from server
      const vapidResponse = await fetch('/api/push/vapid-public-key');
      const { publicKey } = await vapidResponse.json();
      this.vapidPublicKey = publicKey;

      const subscription = await registration.pushManager.subscribe({
        userVisibleOnly: true,
        applicationServerKey: this.urlB64ToUint8Array(this.vapidPublicKey)
      });

      this.subscription = subscription;
      console.log('âœ… Push subscription created:', subscription);

      // Send subscription to server
      await this.sendSubscriptionToServer(subscription);

      return subscription;
    } catch (error) {
      console.error('âŒ Error subscribing to push notifications:', error);
      return null;
    }
  }

  // Unsubscribe from push notifications
  async unsubscribe(): Promise<boolean> {
    try {
      if (this.subscription) {
        await this.subscription.unsubscribe();
        console.log('âœ… Unsubscribed from push notifications');

        // Remove subscription from server
        await fetch('/api/push/unsubscribe', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            endpoint: this.subscription.endpoint
          })
        });

        this.subscription = null;
        return true;
      }
      return false;
    } catch (error) {
      console.error('âŒ Error unsubscribing:', error);
      return false;
    }
  }

  // Send subscription to server
  private async sendSubscriptionToServer(subscription: PushSubscription): Promise<void> {
    try {
      const subscriptionData: PushSubscriptionData = {
        endpoint: subscription.endpoint,
        keys: {
          p256dh: this.arrayBufferToBase64(subscription.getKey('p256dh')!),
          auth: this.arrayBufferToBase64(subscription.getKey('auth')!)
        },
        userId: this.getCurrentUserId(),
        userRole: this.getCurrentUserRole()
      };

      const response = await fetch('/api/push/subscribe', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(subscriptionData)
      });

      if (response.ok) {
        console.log('âœ… Subscription sent to server');
      } else {
        throw new Error('Failed to send subscription to server');
      }
    } catch (error) {
      console.error('âŒ Error sending subscription to server:', error);
    }
  }

  // Get current user ID from localStorage or context
  private getCurrentUserId(): string | undefined {
    const userData = localStorage.getItem('userData');
    if (userData) {
      try {
        const user = JSON.parse(userData);
        return user.id?.toString();
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }
    return undefined;
  }

  // Get current user role
  private getCurrentUserRole(): string {
    const userData = localStorage.getItem('userData');
    if (userData) {
      try {
        const user = JSON.parse(userData);
        return user.role || 'student';
      } catch (error) {
        console.error('Error parsing user data:', error);
      }
    }
    return 'guest';
  }

  // Convert VAPID key
  private urlB64ToUint8Array(base64String: string): Uint8Array {
    const padding = '='.repeat((4 - base64String.length % 4) % 4);
    const base64 = (base64String + padding)
      .replace(/-/g, '+')
      .replace(/_/g, '/');

    const rawData = window.atob(base64);
    const outputArray = new Uint8Array(rawData.length);

    for (let i = 0; i < rawData.length; ++i) {
      outputArray[i] = rawData.charCodeAt(i);
    }
    return outputArray;
  }

  // Convert ArrayBuffer to Base64
  private arrayBufferToBase64(buffer: ArrayBuffer): string {
    const bytes = new Uint8Array(buffer);
    let binary = '';
    for (let i = 0; i < bytes.byteLength; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return window.btoa(binary);
  }

  // Initialize push notifications for user
  async initializeForUser(): Promise<boolean> {
    console.log('ðŸš€ Initializing push notifications for user...');

    if (!this.checkSupport()) {
      return false;
    }

    const permission = this.getPermissionStatus();

    if (permission === 'granted') {
      this.isPermissionGranted = true;
      await this.subscribeToPushNotifications();
      return true;
    } else if (permission === 'default') {
      return await this.requestPermission();
    }

    return false;
  }

  // Show local notification (for testing)
  async showLocalNotification(data: NotificationData): Promise<void> {
    if (!this.isPermissionGranted) {
      console.log('âŒ Notification permission not granted');
      return;
    }

    try {
      const registration = await navigator.serviceWorker.ready;
      await registration.showNotification(data.title, {
        body: data.body,
        icon: data.icon || '/icon-192.png',
        badge: data.badge || '/icon-192.png',
        image: data.image,
        data: data.data,
        tag: data.tag || 'local-notification',
        requireInteraction: true
      });
    } catch (error) {
      console.error('âŒ Error showing local notification:', error);
    }
  }

  // Get current subscription status
  isSubscribed(): boolean {
    return this.subscription !== null;
  }

  // Get supported status
  isNotificationSupported(): boolean {
    return this.isSupported;
  }

  // Listen for notification clicks (in main thread)
  setupNotificationClickHandler(): void {
    if ('serviceWorker' in navigator) {
      navigator.serviceWorker.addEventListener('message', event => {
        if (event.data?.type === 'NOTIFICATION_CLICKED') {
          console.log('Notification clicked in main thread:', event.data);

          // Handle navigation or other actions
          if (event.data.url && event.data.url !== '/') {
            window.location.href = event.data.url;
          }
        }
      });
    }
  }
}

// Export singleton instance
export const pushNotificationManager = new PushNotificationManager();

// Export types
export type { PushSubscriptionData, NotificationData };

