import { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Bell, X, Smartphone } from 'lucide-react';
import { useNotifications } from '@/hooks/useNotifications';

interface NotificationPermissionPromptProps {
  onDismiss?: () => void;
}

export default function NotificationPermissionPrompt({ onDismiss }: NotificationPermissionPromptProps) {
  const { requestPermission, isLoading, dismissPermissionPrompt } = useNotifications();
  const [isVisible, setIsVisible] = useState(true);

  const handleEnable = async () => {
    const success = await requestPermission();
    if (success) {
      setIsVisible(false);
      onDismiss?.();
    }
  };

  const handleDismiss = () => {
    dismissPermissionPrompt();
    setIsVisible(false);
    onDismiss?.();
  };

  if (!isVisible) {
    return null;
  }

  return (
    <Card 
      className="mx-4 mb-4 border-orange-200 bg-gradient-to-r from-orange-50 to-amber-50 dark:from-orange-950/30 dark:to-amber-950/30 shadow-md"
      data-testid="notification-permission-prompt"
    >
      <CardContent className="p-4">
        <div className="flex items-start gap-3">
          <div className="flex-shrink-0 mt-1">
            <div className="w-10 h-10 bg-orange-100 dark:bg-orange-900/50 rounded-full flex items-center justify-center">
              <Bell className="w-5 h-5 text-orange-600 dark:text-orange-400" />
            </div>
          </div>

          <div className="flex-1 min-w-0">
            <h3 className="font-semibold text-gray-900 dark:text-gray-100 mb-1">
              Stay updated with your orders
            </h3>
            <p className="text-sm text-gray-600 dark:text-gray-300 mb-3">
              Get instant notifications when your order status changes - from preparation to ready for pickup.
            </p>

            <div className="flex items-center gap-2 text-xs text-gray-500 dark:text-gray-400 mb-3">
              <Smartphone className="w-4 h-4" />
              <span>Works on mobile and desktop</span>
            </div>

            <div className="flex gap-2">
              <Button
                onClick={handleEnable}
                disabled={isLoading}
                size="sm"
                className="bg-orange-600 hover:bg-orange-700 text-white"
                data-testid="button-enable-notifications"
              >
                {isLoading ? 'Enabling...' : 'Enable Notifications'}
              </Button>

              <Button
                onClick={handleDismiss}
                variant="ghost"
                size="sm"
                className="text-gray-600 dark:text-gray-400"
                data-testid="button-dismiss-notifications"
              >
                Maybe later
              </Button>
            </div>
          </div>

          <button
            onClick={handleDismiss}
            className="flex-shrink-0 p-1 text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors"
            data-testid="button-close-notification-prompt"
          >
            <X className="w-4 h-4" />
          </button>
        </div>
      </CardContent>
    </Card>
  );
}

