import { useState, useEffect, useCallback } from 'react';
import { requestNotificationPermission, onMessageListener, getNotificationToken, storeNotificationToken } from '@/lib/firebase';
import { useAuth } from './useAuth';
import { toast } from 'sonner';

interface NotificationState {
  permission: NotificationPermission;
  token: string | null;
  isSupported: boolean;
  isLoading: boolean;
}

export function useNotifications() {
  const { user, isAuthenticated } = useAuth();
  const [notificationState, setNotificationState] = useState<NotificationState>({
    permission: 'default',
    token: null,
    isSupported: false,
    isLoading: true
  });

  // Check notification support and current permission
  useEffect(() => {
    const checkNotificationSupport = async () => {
      if (typeof window === 'undefined' || !('Notification' in window)) {
        setNotificationState(prev => ({
          ...prev,
          isSupported: false,
          isLoading: false
        }));
        return;
      }

      // iOS detection
      const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent);
      const isIOSSafari = isIOS && !(navigator as any).standalone && !/CriOS|FxiOS|EdgiOS/.test(navigator.userAgent);

      // iOS Safari doesn't support push notifications unless it's a PWA
      if (isIOSSafari) {
        setNotificationState(prev => ({
          ...prev,
          isSupported: false,
          isLoading: false
        }));
        return;
      }

      setNotificationState(prev => ({
        ...prev,
        permission: Notification.permission,
        isSupported: true,
        isLoading: false
      }));

      // If already granted, get token
      if (Notification.permission === 'granted') {
        try {
          const token = await getNotificationToken();
          if (token) {
            setNotificationState(prev => ({
              ...prev,
              token
            }));

            // Store token with user association
            if (isAuthenticated && user) {
              await storeNotificationToken(token, user.id);
            }
          }
        } catch (error) {
          console.error('Error getting notification token:', error);
        }
      }
    };

    checkNotificationSupport();
  }, []);

  // Listen for foreground notifications
  useEffect(() => {
    if (notificationState.permission === 'granted') {
      // Set up foreground notification listener
      import('../lib/firebase').then(({ onMessageListener }) => {
        onMessageListener().then((payload: any) => {
          console.log('Foreground notification received:', payload);
          // The notification display is handled in firebase.ts
        }).catch((error: any) => {
          console.error('Error setting up foreground notification listener:', error);
        });
      });
    }
  }, [isAuthenticated, user]);

  // Listen for foreground messages
  useEffect(() => {
    if (notificationState.isSupported && notificationState.permission === 'granted') {
      const unsubscribe = onMessageListener()
        .then((payload: any) => {
          console.log('Foreground message received:', payload);

          // Show in-app notification
          toast(payload.notification?.title || 'Order Update', {
            description: payload.notification?.body || 'Your order status has been updated',
            action: payload.data?.orderId ? {
              label: 'View Order',
              onClick: () => {
                window.location.href = `/order-status/${payload.data.orderId}`;
              }
            } : undefined,
            duration: 10000
          });
        })
        .catch((error: any) => {
          console.error('Error listening for messages:', error);
        });

      return () => {
        // Cleanup if needed
      };
    }
  }, [notificationState.permission, notificationState.isSupported]);

  // Request notification permission
  const requestPermission = useCallback(async (): Promise<boolean> => {
    if (!notificationState.isSupported) {
      toast.error('Push notifications are not supported on this device');
      return false;
    }

    if (notificationState.permission === 'granted') {
      return true;
    }

    try {
      setNotificationState(prev => ({ ...prev, isLoading: true }));

      const token = await requestNotificationPermission();

      if (token) {
        setNotificationState(prev => ({
          ...prev,
          permission: 'granted',
          token,
          isLoading: false
        }));

        // Store token with user association
        if (isAuthenticated && user) {
          await storeNotificationToken(token, user.id);
        }

        toast.success('Push notifications enabled! You\'ll receive order updates.');
        return true;
      } else {
        setNotificationState(prev => ({
          ...prev,
          permission: Notification.permission,
          isLoading: false
        }));

        if (Notification.permission === 'denied') {
          toast.error('Push notifications blocked. Please enable them in browser settings.');
        } else {
          toast.error('Unable to enable push notifications');
        }
        return false;
      }
    } catch (error) {
      console.error('Error requesting notification permission:', error);
      setNotificationState(prev => ({ ...prev, isLoading: false }));
      toast.error('Error enabling push notifications');
      return false;
    }
  }, [notificationState.isSupported, notificationState.permission, isAuthenticated, user]);

  // Store notification token on server
  const storeNotificationToken = async (token: string, userId: string | number): Promise<void> => {
    try {
      const response = await fetch('/api/users/notification-token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          token,
          deviceInfo: {
            userAgent: navigator.userAgent,
            platform: navigator.platform,
            timestamp: new Date().toISOString()
          }
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to store notification token');
      }

      console.log('Notification token stored successfully');
    } catch (error) {
      console.error('Error storing notification token:', error);
      // Don't show user error - this is background operation
    }
  };

  // Check if should show permission prompt
  const shouldShowPermissionPrompt = useCallback((): boolean => {
    if (!notificationState.isSupported) return false;
    if (notificationState.permission === 'granted') return false;
    if (notificationState.permission === 'denied') return false;

    // Check if user has dismissed prompt recently
    const dismissedKey = 'notification_prompt_dismissed';
    const lastDismissed = localStorage.getItem(dismissedKey);

    if (lastDismissed) {
      const dismissedTime = parseInt(lastDismissed);
      const oneDayAgo = Date.now() - (24 * 60 * 60 * 1000);

      // Don't show again for 24 hours after dismissal
      if (dismissedTime > oneDayAgo) {
        return false;
      }
    }

    return true;
  }, [notificationState.isSupported, notificationState.permission]);

  // Dismiss permission prompt
  const dismissPermissionPrompt = useCallback(() => {
    const dismissedKey = 'notification_prompt_dismissed';
    localStorage.setItem(dismissedKey, Date.now().toString());
  }, []);

  return {
    ...notificationState,
    requestPermission,
    shouldShowPermissionPrompt: shouldShowPermissionPrompt(),
    dismissPermissionPrompt
  };
}

server/notification-service.ts.content:
import { storage } from './storage-hybrid';

// Firebase Admin SDK types for push notifications
interface NotificationPayload {
  title: string;
  body: string;
  data?: Record<string, string>;
}

interface PushNotificationData {
  orderId: string;
  orderNumber: string;
  status: string;
  customerName: string;
  estimatedTime?: number;
}

class NotificationService {
  private firebaseAdmin: any = null;

  constructor() {
    this.initializeFirebase();
  }

  private async initializeFirebase() {
    try {
      // Import Firebase Admin SDK dynamically to avoid errors if not configured
      const admin = await import('firebase-admin');

      // Check if Firebase is already initialized
      if (admin.apps && admin.apps.length === 0) {
        // Initialize Firebase Admin with service account
        const serviceAccount = {
          type: "service_account",
          project_id: process.env.FIREBASE_PROJECT_ID,
          private_key_id: process.env.FIREBASE_PRIVATE_KEY_ID,
          private_key: process.env.FIREBASE_PRIVATE_KEY?.replace(/\\n/g, '\n'),
          client_email: process.env.FIREBASE_CLIENT_EMAIL,
          client_id: process.env.FIREBASE_CLIENT_ID,
          auth_uri: "https://accounts.google.com/o/oauth2/auth",
          token_uri: "https://oauth2.googleapis.com/token",
          auth_provider_x509_cert_url: "https://www.googleapis.com/oauth2/v1/certs",
          client_x509_cert_url: `https://www.googleapis.com/robot/v1/metadata/x509/${process.env.FIREBASE_CLIENT_EMAIL}`
        } as any;

        admin.initializeApp({
          credential: admin.credential.cert(serviceAccount),
          projectId: process.env.FIREBASE_PROJECT_ID
        });

        console.log('Firebase Admin SDK initialized successfully');
      }

      this.firebaseAdmin = admin;
    } catch (error) {
      console.warn('Firebase Admin SDK initialization failed:', error);
      console.log('Push notifications will be disabled. Add Firebase service account credentials to enable.');
    }
  }

  async sendOrderStatusNotification(data: PushNotificationData): Promise<boolean> {
    if (!this.firebaseAdmin) {
      console.log('ðŸ“± Firebase not configured - skipping push notification');
      return false;
    }

    try {
      // Get user notification token
      let user = null;

      // Find user by customer name (fallback method)
      const users = await storage.getAllUsers();
      user = users.find(u => u.name.toLowerCase() === data.customerName.toLowerCase());

      if (!user || !user.notificationToken || !user.notificationEnabled) {
        console.log(`ðŸ“± No notification token found for user: ${data.customerName}`);
        return false;
      }

      // Create notification payload based on order status
      const notification = this.createStatusNotification(data);

      const message = {
        token: user.notificationToken,
        notification: {
          title: notification.title,
          body: notification.body
        },
        data: {
          orderId: data.orderId,
          orderNumber: data.orderNumber,
          status: data.status,
          customerName: data.customerName,
          type: 'order_status_update'
        },
        android: {
          notification: {
            icon: 'ic_notification',
            color: '#f97316', // Orange color
            channelId: 'order_updates',
            priority: 'high' as const,
            defaultSound: true
          }
        },
        apns: {
          payload: {
            aps: {
              alert: {
                title: notification.title,
                body: notification.body
              },
              badge: 1,
              sound: 'default'
            }
          }
        },
        webpush: {
          notification: {
            title: notification.title,
            body: notification.body,
            icon: '/icon-192.png',
            badge: '/icon-192.png',
            tag: data.orderId,
            requireInteraction: true,
            actions: [
              {
                action: 'view-order',
                title: 'View Order'
              }
            ]
          },
          fcmOptions: {
            link: `/order-status/${data.orderId}`
          }
        }
      };

      const response = await this.firebaseAdmin.messaging().send(message);
      console.log(`ðŸ“± Push notification sent successfully to ${data.customerName}:`, response);
      return true;

    } catch (error: any) {
      console.error(`ðŸ“± Failed to send push notification to ${data.customerName}:`, error);

      // If token is invalid, clear it from database
      if (error.code === 'messaging/registration-token-not-registered' || 
          error.code === 'messaging/invalid-registration-token') {
        await this.clearInvalidToken(data.customerName);
      }

      return false;
    }
  }

  private createStatusNotification(data: PushNotificationData): NotificationPayload {
    const { status, orderNumber, estimatedTime } = data;

    switch (status.toLowerCase()) {
      case 'pending':
        return {
          title: 'ðŸ”„ Order Received',
          body: `Your order #${orderNumber} is being prepared. Estimated time: ${estimatedTime || 15} minutes.`
        };

      case 'preparing':
        return {
          title: 'ðŸ‘¨â€ðŸ³ Order in Kitchen',
          body: `Your order #${orderNumber} is being cooked with care. Almost ready!`
        };

      case 'ready':
        return {
          title: 'âœ… Order Ready!',
          body: `Your order #${orderNumber} is ready for pickup at the counter.`
        };

      case 'delivered':
        return {
          title: 'ðŸŽ‰ Order Delivered',
          body: `Your order #${orderNumber} has been delivered. Enjoy your meal!`
        };

      case 'cancelled':
        return {
          title: 'âŒ Order Cancelled',
          body: `Your order #${orderNumber} has been cancelled. Please contact support if you have questions.`
        };

      default:
        return {
          title: 'ðŸ“± Order Update',
          body: `Your order #${orderNumber} status has been updated to: ${status}`
        };
    }
  }

  private async clearInvalidToken(customerName: string): Promise<void> {
    try {
      const users = await storage.getAllUsers();
      const user = users.find(u => u.name.toLowerCase() === customerName.toLowerCase());

      if (user) {
        await storage.updateUser(user.id, {
          notificationToken: null,
          notificationEnabled: false
        });
        console.log(`ðŸ“± Cleared invalid notification token for user: ${customerName}`);
      }
    } catch (error) {
      console.error('ðŸ“± Error clearing invalid token:', error);
    }
  }

  private async clearInvalidTokenByEmail(userEmail: string): Promise<void> {
    try {
      const user = await storage.getUserByEmail(userEmail);

      if (user) {
        await storage.updateUser(user.id, {
          notificationToken: null,
          notificationEnabled: false
        });
        console.log(`ðŸ“± Cleared invalid notification token for user: ${userEmail}`);
      }
    } catch (error) {
      console.error('ðŸ“± Error clearing invalid token:', error);
    }
  }

  // Test notification method
  async sendTestNotification(userEmail: string): Promise<boolean> {
    if (!this.firebaseAdmin) {
      console.log('Firebase not configured - cannot send test notification');
      return false;
    }

    try {
      const user = await storage.getUserByEmail(userEmail);

      if (!user || !user.notificationToken) {
        console.log(`No notification token found for user: ${userEmail}`);
        return false;
      }

      const message = {
        token: user.notificationToken,
        notification: {
          title: 'Test Notification',
          body: 'Firebase push notifications are working correctly!'
        },
        data: {
          type: 'test',
          timestamp: new Date().toISOString()
        },
        webpush: {
          notification: {
            title: 'Test Notification',
            body: 'Firebase push notifications are working correctly!',
            icon: '/icon-192.png',
            badge: '/icon-192.png',
            tag: 'test-notification',
            requireInteraction: true
          }
        },
        android: {
          priority: 'high' as const,
          notification: {
            title: 'Test Notification',
            body: 'Firebase push notifications are working correctly!',
            icon: '/icon-192.png',
            sound: 'default',
            tag: 'test-notification'
          }
        }
      };

      const response = await this.firebaseAdmin.messaging().send(message);
      console.log(`Test notification sent successfully: ${response}`);
      return true;

    } catch (error: any) {
      console.error(`Failed to send test notification:`, error);

      // If token is invalid, clear it from database
      if (error.code === 'messaging/registration-token-not-registered' || 
          error.code === 'messaging/invalid-registration-token') {
        console.log(`Clearing invalid token for user: ${userEmail}`);
        await this.clearInvalidTokenByEmail(userEmail);
      }

      return false;
    }
  }


}

