client/src/components/iPhoneNotification.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { AlertTriangle, CheckCircle, RefreshCw, Smartphone } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { requestNotificationPermission, storeNotificationToken } from "@/lib/firebase";

export default function iPhoneNotificationHelper() {
  const [isGettingToken, setIsGettingToken] = useState(false);
  const [tokenStatus, setTokenStatus] = useState<'none' | 'getting' | 'success' | 'error'>('none');
  const [debugInfo, setDebugInfo] = useState<any>(null);
  const { toast } = useToast();
  const { user } = useAuth();

  const getNewFirebaseToken = async () => {
    if (!user) {
      toast({
        title: "Please log in first",
        variant: "destructive"
      });
      return;
    }

    setIsGettingToken(true);
    setTokenStatus('getting');

    try {
      console.log('ðŸ”„ Requesting new Firebase token for iPhone PWA...');

      // Request permission and get token
      const token = await requestNotificationPermission();

      if (token) {
        // Store token on server
        await storeNotificationToken(token, user.id);

        setTokenStatus('success');
        toast({
          title: "Firebase token obtained!",
          description: "You can now test Firebase notifications. Make sure to minimize the app first.",
        });

        console.log('âœ… New Firebase token stored successfully');
      } else {
        setTokenStatus('error');
        toast({
          title: "Failed to get Firebase token",
          description: "Check console for details",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('âŒ Failed to get Firebase token:', error);
      setTokenStatus('error');
      toast({
        title: "Error getting Firebase token",
        description: "Check console for details",
        variant: "destructive"
      });
    } finally {
      setIsGettingToken(false);
    }
  };

  const getDebugInfo = async () => {
    if (!user) return;

    try {
      const response = await fetch('/api/notifications/debug', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userEmail: user.email }),
      });

      const data = await response.json();
      setDebugInfo(data);
      console.log('ðŸ” Debug info:', data);
    } catch (error) {
      console.error('Failed to get debug info:', error);
    }
  };

  const testFirebaseNotification = async () => {
    if (!user) return;

    try {
      toast({
        title: "Notification will send in 5 seconds",
        description: "Minimize your app now! Countdown starting..."
      });

      // Start countdown
      for (let i = 5; i > 0; i--) {
        toast({
          title: `Sending notification in ${i} seconds`,
          description: "Minimize your app now to receive it!"
        });
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      // Send the notification after delay
      const response = await fetch('/api/notifications/test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userEmail: user.email }),
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Firebase notification sent!",
          description: "Check your device for the notification"
        });
      } else {
        toast({
          title: "Failed to send notification",
          description: data.error || "Unknown error",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('Failed to test notification:', error);
      toast({
        title: "Error sending notification",
        variant: "destructive"
      });
    }
  };

  const testBackgroundSimulation = async () => {
    if (!user) return;

    try {
      toast({
        title: "Testing background simulation",
        description: "Sending data-only Firebase message to test service worker..."
      });

      const response = await fetch('/api/notifications/fake-background-test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userEmail: user.email }),
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Background test sent!",
          description: "If service worker is working, you should see a notification"
        });
      } else {
        toast({
          title: "Failed to send background test",
          description: data.error || "Unknown error",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('Failed to test background simulation:', error);
      toast({
        title: "Error sending background test",
        variant: "destructive"
      });
    }
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Smartphone className="w-5 h-5" />
          iPhone PWA Notification Helper
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="bg-blue-50 dark:bg-blue-950 p-4 rounded-lg">
          <h3 className="font-semibold mb-2 flex items-center gap-2">
            <AlertTriangle className="w-4 h-4" />
            iPhone PWA Notification Requirements
          </h3>
          <ul className="text-sm space-y-1 list-disc list-inside">
            <li>App must be added to home screen (PWA mode)</li>
            <li>Notification permission must be granted</li>
            <li>Firebase token must be fresh (expires during development)</li>
            <li>App must be minimized when testing notifications</li>
          </ul>
        </div>

        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium">Step 1: Get Fresh Firebase Token</h4>
              <p className="text-sm text-muted-foreground">Required for iPhone PWA notifications</p>
            </div>
            <Button
              onClick={getNewFirebaseToken}
              disabled={isGettingToken}
              size="sm"
              data-testid="button-get-firebase-token"
            >
              {isGettingToken ? (
                <>
                  <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                  Getting Token...
                </>
              ) : (
                'Get Firebase Token'
              )}
            </Button>
          </div>

          {tokenStatus === 'success' && (
            <div className="flex items-center gap-2 text-green-600 text-sm">
              <CheckCircle className="w-4 h-4" />
              Firebase token obtained successfully!
            </div>
          )}

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium">Step 2: Test Firebase Notification</h4>
              <p className="text-sm text-muted-foreground">5-second delay - time to minimize app</p>
            </div>
            <Button
              onClick={testFirebaseNotification}
              disabled={tokenStatus !== 'success'}
              size="sm"
              variant="outline"
              data-testid="button-test-firebase-notification"
            >
              Test with 5s Delay
            </Button>
          </div>

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium">Step 3: Background Simulation Test</h4>
              <p className="text-sm text-muted-foreground">Tests service worker when app appears closed</p>
            </div>
            <Button
              onClick={testBackgroundSimulation}
              disabled={tokenStatus !== 'success'}
              size="sm"
              variant="secondary"
              data-testid="button-test-background-simulation"
            >
              Fake App Closed
            </Button>
          </div>

          <div className="flex items-center justify-between">
            <div>
              <h4 className="font-medium">Debug Info</h4>
              <p className="text-sm text-muted-foreground">Check token status and configuration</p>
            </div>
            <Button
              onClick={getDebugInfo}
              size="sm"
              variant="ghost"
              data-testid="button-debug-info"
            >
              Get Debug Info
            </Button>
          </div>
        </div>

        {debugInfo && (
          <div className="bg-gray-50 dark:bg-gray-900 p-4 rounded-lg">
            <h4 className="font-medium mb-2">Debug Information</h4>
            <pre className="text-xs overflow-auto">
              {JSON.stringify(debugInfo, null, 2)}
            </pre>
          </div>
        )}

        <div className="bg-yellow-50 dark:bg-yellow-950 p-4 rounded-lg">
          <h4 className="font-semibold mb-2">Testing Instructions</h4>
          <ol className="text-sm space-y-1 list-decimal list-inside">
            <li>Click "Get Firebase Token" and allow notification permission</li>
            <li>Wait for "Firebase token obtained successfully!" message</li>
            <li>Click "Test with 5s Delay"</li>
            <li><strong>You have 5 seconds to minimize your PWA or go to home screen</strong></li>
            <li>The notification will be sent automatically after the countdown</li>
            <li>Wait for the notification to appear on your device</li>
          </ol>
        </div>
      </CardContent>
    </Card>
  );
}

